import { Dispatch, SetStateAction, useEffect, useState } from "react";
import { Container } from "../Common";
import { Question, startAudioRecordingId, Station } from "../types";
import recordingStartAudioUrl from "../media/start.mp3";
import { Transition } from "react-transition-group";
import QuestionContent from "./QuestionContent";
import Header from "../Header";
import { useHistory } from "react-router-dom";
import { ReactMediaRecorderRenderProps, useReactMediaRecorder } from "react-media-recorder";
import { playStartTone } from "../hooks";
import { copy } from "../../ECGPage/Functions/utility";

type QuestionProps = {
    station: Station;
    questionIdx: { prev: number; curr: number };
    updateStation: Dispatch<SetStateAction<Station>>;
    updateQuestionIdx: Dispatch<SetStateAction<{ curr: number; prev: number }>>;
    handleExit: (questions: Question[], tok: string) => void;
};

const QuestionPage = (props: QuestionProps) => {
    const questionCount = props.station.questions.length;
    const history = useHistory();
    const idx = props.questionIdx;

    const nextFunc = () =>
        idx.curr === questionCount - 1
            ? history.push("/OSCE/Station/Summary")
            : props.updateQuestionIdx({
                  prev: idx.curr,
                  curr: idx.curr + 1,
              });

    const prevFunc = () =>
        idx.curr === 0
            ? {}
            : props.updateQuestionIdx({
                  prev: idx.curr,
                  curr: idx.curr - 1,
              });

    const [recorders, updateRecorders] = useState<ReactMediaRecorderRenderProps[]>([]);

    useEffect(() => {
        updateRecorders(rcrds => {
            for (let idx = 0; idx < props.station.questions.length; idx++) {}
            return rcrds;
        });
    }, []);
    Array(props.station.questions.length).map((_, idx) => {
        return useReactMediaRecorder({
            audio: true,
            onStop: (blobUrl, _) => {
                playStartTone();
                props.updateStation(s => {
                    s.questions[idx].responseAudio = blobUrl ? blobUrl : "";
                    return copy(s); // because object is nested, react shallow compares
                });

                // advance question on recording completetion
                props.updateQuestionIdx(qi => ({
                    prev: qi.curr,
                    curr: idx + 1,
                }));
            },
        });
    });

    const transitionStyles: { [key: string]: any } =
        idx.curr < idx.prev
            ? {
                  entering: { transform: "translateY(-150%)" },
                  entered: { transform: "translateY(0%)" },
                  exiting: { transform: "translateY(150%)" },
                  exited: { transform: "translateY(-150%)" },
              }
            : {
                  entering: { transform: "translateY(150%)" },
                  entered: { transform: "translateY(0%)" },
                  exiting: { transform: "translateY(-150%)" },
                  exited: { transform: "translateY(150%)" },
              };

    return (
        <Container>
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "1fr",
                }}
            >
                <Header
                    updateStation={props.updateStation}
                    station={props.station}
                    handleExit={props.handleExit}
                    questionIdx={idx}
                    title={"Station"}
                    percentage={10 + (100 * idx.curr) / questionCount}
                    timer={true}
                    nextFunc={nextFunc}
                    prevFunc={prevFunc}
                />

                {props.station.questions.map((question, questionIdx) => (
                    <Transition
                        in={questionIdx === idx.curr}
                        timeout={500}
                        unmountOnExit
                        mountOnEnter
                    >
                        {state => (
                            <QuestionContent
                                question={question}
                                updateStation={props.updateStation}
                                questionIdx={questionIdx}
                                updateQuestionIdx={props.updateQuestionIdx}
                                transitionStyle={transitionStyles[state]}
                            />
                        )}
                    </Transition>
                ))}
            </div>

            <audio src={recordingStartAudioUrl} id={startAudioRecordingId} />
        </Container>
    );
};

export default QuestionPage;
