import { Dispatch, SetStateAction, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { Box, MoveOn, Container } from "./Common";
import TaskSection from "./TaskSection";
import { promptId, Question, responseId, startAudioRecordingId, Station } from "./types";
import QuestionPrompt from "./QuestionPrompt";
import { generateWaveSurfer, playStartTone } from "./hooks";
import recordingStartAudioUrl from "./media/start.mp3";
import { useReactMediaRecorder } from "react-media-recorder";
import { CSSTransition, Transition } from "react-transition-group";
import { copy } from "../ECGPage/Functions/utility";

type QuestionProps = {
    station: Station;
    questionIdx: { prev: number; curr: number };
    updateStation: Dispatch<SetStateAction<Station>>;
    updateQuestionIdx: Dispatch<SetStateAction<{ curr: number; prev: number }>>;
};

type AudioState = {
    promptAudio: HTMLAudioElement | null;
    responseWaveSurfer: WaveSurfer | undefined;
    promptPlaying: boolean;
    responsePlaying: boolean;
    recording: boolean;
};

const initialAudioState: AudioState = {
    promptAudio: null,
    responseWaveSurfer: undefined,
    promptPlaying: false,
    responsePlaying: false,
    recording: false,
};

const QuestionPage = (props: QuestionProps) => {
    const [imageExpanded, updateImageExpanded] = useState(false);
    const [audioState, updateAudioState] = useState<AudioState>(initialAudioState);
    const [loadingRecorder, updateLoadingRecorder] = useState(false);

    const { startRecording, stopRecording } = useReactMediaRecorder({
        audio: true,
        onStart: () => {
            updateLoadingRecorder(false);
            updateAudioState(as => ({ ...as, recording: true }));
            playStartTone();
        },
        onStop: (blobUrl, _) => {
            playStartTone();
            props.updateStation(s => {
                s.questions[props.questionIdx.curr].responseAudio = blobUrl ? blobUrl : "";
                return copy(s); // because object is nested, react shallow compares
            });
            if (props.questionIdx.curr !== props.station.questions.length - 1) {
                props.updateQuestionIdx({
                    prev: props.questionIdx.curr,
                    curr: props.questionIdx.curr + 1,
                });
            }
        },
    });

    const question = props.station.questions[props.questionIdx.curr];

    const stopEverything = () => {
        console.log("stop everything");
        audioState.promptAudio?.pause();
        audioState.responseWaveSurfer?.stop();
        if (audioState.recording) stopRecording();
        updateAudioState(as => ({
            ...as,
            promptPlaying: false,
            responsePlaying: false,
            recording: false,
        }));
    };

    const play = (id: string) => {
        stopEverything();
        updateAudioState(as => ({
            ...as,
            promptPlaying: id === promptId,
            responsePlaying: id === responseId,
        }));

        if (id === promptId) audioState.promptAudio?.play();
        else audioState.responseWaveSurfer?.play();
    };

    const record = () => {
        stopEverything();
        updateLoadingRecorder(true);
        startRecording();
    };

    useEffect(() => {
        const container = document.getElementById("waveform");
        if (container) container.innerHTML = "";

        updateAudioState(as => ({
            ...as,
            responseWaveSurfer: generateWaveSurfer(
                "waveform",
                question.responseAudio,
                stopEverything
            ),
        }));
    }, [question.responseAudio]);

    // play prompt whenever questionchanges
    useEffect(() => {
        console.log("rerender");
        const promptAudio = document.getElementById(promptId) as HTMLAudioElement;
        promptAudio.addEventListener("ended", _ => {
            updateAudioState(as => ({ ...as, promptPlaying: false }));
        });
        promptAudio.play();
        updateAudioState(as => ({
            ...as,
            promptAudio,
            promptPlaying: true,
        }));
    });

    // cease playback when compnent unmounts <=== six hour bug :)
    useEffect(() => stopEverything, [audioState.promptAudio, audioState.responseWaveSurfer]);

    const defaultStyle = {
        gridRowStart: 1,
        gridColumnStart: 1,
        transition: `transform ${500}ms`,
    };

    const transitionStyles: { [key: string]: any } =
        props.questionIdx.curr < props.questionIdx.prev
            ? {
                  entering: { transform: "translateY(-150%)" },
                  entered: { transform: "translateY(0%)" },
                  exiting: { transform: "translateY(150%)" },
                  exited: { transform: "translateY(-150%)" },
              }
            : {
                  entering: { transform: "translateY(150%)" },
                  entered: { transform: "translateY(0%)" },
                  exiting: { transform: "translateY(-150%)" },
                  exited: { transform: "translateY(150%)" },
              };

    return (
        <Container className="page">
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "1fr",
                }}
            >
                {props.station.questions.map((q, questionIdx) => (
                    <Transition
                        in={questionIdx === props.questionIdx.curr}
                        timeout={500}
                        mountOnEnter
                        unmountOnExit
                    >
                        {state => (
                            <div
                                style={{
                                    ...defaultStyle,
                                    ...transitionStyles[state],
                                }}
                            >
                                <Box style={{ textAlign: "center" }}>
                                    <QuestionPrompt
                                        question={question}
                                        toggle={() => {
                                            audioState.promptPlaying
                                                ? stopEverything()
                                                : play(promptId);
                                        }}
                                    />

                                    <br></br>

                                    <Img src={question.img} imageExpanded={imageExpanded}></Img>
                                    <br />
                                </Box>
                            </div>
                        )}
                    </Transition>
                ))}

                <div
                    style={{
                        gridRowStart: 2,
                        zIndex: 1000,
                        backgroundColor: "#1c2124",
                        height: "100vh",
                    }}
                >
                    <TaskSection
                        recording={audioState.recording}
                        recordResponse={record}
                        playing={audioState.responsePlaying}
                        playResponse={() => play(responseId)}
                        stopEverything={stopEverything}
                        responseAvailable={question.responseAudio !== ""}
                        loadingRecorder={loadingRecorder}
                    />
                </div>

                <audio src={recordingStartAudioUrl} id={startAudioRecordingId} />
            </div>
        </Container>
    );
};

export default QuestionPage;

const Img = styled.img<{ imageExpanded: boolean }>`
    display: block;
    margin: auto;
    width: ${props => (props.imageExpanded ? "100%" : "60%")};
    transition: width 0.2s;
`;
