import { Dispatch, SetStateAction, useContext, useEffect, useState } from "react";
import styled from "styled-components";
import MetaContext from "../../MetaContext";
import { Login, LoginButton } from "../HomePage/LoginLogout";
import { StartButton } from "./Common";
import TimerBar from "./TimerBar";
import { HeaderDetails, Question, Station } from "./types";
import htmlParser from "react-html-parser";
import { Link, useLocation } from "react-router-dom";
import AlphaTimer from "./AlphaTimer";
import { headerDetailSwitch } from "./headerDetailsSwitch";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";

type HeaderProps = {
    station: Station;
    handleExit: (questions: Question[], tok: string) => void;
    updateQuestionIdx: Dispatch<SetStateAction<number>>;
    questionIdx: number;
};

const DefaultDetails: HeaderDetails = {
    title: "Brief",
    percentage: 5,
    briefAndExit: false,
    timer: false,
    pathToNext: "/OSCE",
    pathToPrev: "/OSCE",
};

const Header = (props: HeaderProps) => {
    const { token } = useContext(MetaContext);
    const [displayLoginOption, updateDisplayLoginOption] = useState(false);
    const [headerDetails, updateHeaderDetails] = useState(DefaultDetails);

    const location = useLocation();
    const splitPaths = location.pathname.split("/");
    const lastPath = splitPaths[splitPaths.length - 1];

    useEffect(() => {
        updateHeaderDetails(headerDetailSwitch(lastPath, props.station.questions.length));
    }, [location.pathname]);

    return (
        <HeaderContainer>
            <div
                style={{
                    display: "flex",
                    justifyContent: "space-between",
                    height: "40px",
                    alignItems: "center",
                }}
            >
                <div style={{ width: "33%" }}>
                    {headerDetails.briefAndExit && (
                        <StartButton
                            onClick={() =>
                                token
                                    ? props.handleExit(props.station?.questions, token)
                                    : updateDisplayLoginOption(true)
                            }
                        >
                            Exit Demo
                        </StartButton>
                    )}
                </div>

                <div style={{ width: "33%", textAlign: "center" }}>
                    <h2>{headerDetails.title}</h2>
                </div>

                <div style={{ width: "33%", textAlign: "right" }}>
                    <Arrow onClick={() => props.updateQuestionIdx()}>
                        <FaArrowLeft />
                    </Arrow>
                    {headerDetails.timer && (
                        <AlphaTimer
                            startTime={props.station.timePermitted}
                            pathToNext={headerDetails.pathToNext}
                        />
                    )}
                    <Arrow>
                        <FaArrowRight />
                    </Arrow>
                </div>
            </div>

            <TimerBar percentage={headerDetails.percentage} />

            <br />

            {displayLoginOption && (
                <div style={{ textAlign: "center" }}>
                    <Login
                        label={"Login & Save!"}
                        successCallback={t => props.handleExit(props.station.questions, t)}
                    />
                    <Link to="/OSCE">
                        <LoginButton>Exit without saving.</LoginButton>
                    </Link>
                </div>
            )}
        </HeaderContainer>
    );
};

export default Header;

const HeaderContainer = styled.div`
    position: sticky;
    top: 0;
    max-width: 800px;
    margin: auto;
    background-color: #1c2124;
    padding: 20px 20px 0 20px;
    opacity: 0.98;
    z-index: 999;
`;

export const Arrow = styled.button`
    text-align: center;
    border: 2px solid grey;
    width: 50px;
    color: #fad000;
    background-color: transparent;
    border-radius: 2px;
    height: max-content;
`;
