import { Dispatch, SetStateAction, useContext, useEffect, useState } from "react";
import {
    ReactMediaRecorderRenderProps,
    StatusMessages,
    useReactMediaRecorder,
} from "react-media-recorder";
import styled from "styled-components";
import { copy } from "../../../old-components/ECGPage/Functions/utility";
import { Box } from "../Common";
import { playStartTone } from "../hooks";
import QuestionPrompt from "../QuestionPrompt";
import RecordControl from "./RecordControl";
import WaveSurfer from "./WaveSurfer";
import { promptId, Question, responseId, Station } from "../types";
import MetaContext from "../../../MetaContext";

type QuestionContentProps = {
    question: Question;
    updateStation: Dispatch<SetStateAction<Station>>;
    questionIdx: number;
    updateQuestionIdx: Dispatch<SetStateAction<{ prev: number; curr: number }>>;
    transitionStyle: any;
    nextFunc: () => void;
};

export type AudioState = {
    responseWaveSurfer: WaveSurfer | undefined;
};

const getPromptAudio = (idx: number) =>
    document.getElementById(promptId + idx) as HTMLAudioElement | null;

const QuestionContent = (props: QuestionContentProps) => {
    const { narrowWindow } = useContext(MetaContext);
    const [audioState, updateAudioState] = useState<AudioState>({
        responseWaveSurfer: undefined,
    });

    const { startRecording, stopRecording, status } = useReactMediaRecorder({
        audio: true,
        onStop: (blobUrl, _) => {
            playStartTone();
            props.updateStation(s => {
                s.questions[props.questionIdx].responseAudio = blobUrl ? blobUrl : "";
                return copy(s); // because object is nested, react shallow compares
            });

            // advance question on recording completetion
            props.nextFunc();
        },
    });

    const recording = status === "recording";
    const promptAudio = document.getElementById(
        promptId + props.questionIdx
    ) as HTMLAudioElement | null;
    const promptPlaying = promptAudio ? !promptAudio.paused : false;
    const responsePlaying = audioState.responseWaveSurfer
        ? audioState.responseWaveSurfer.isPlaying()
        : false;

    const stopEverything = () => {
        promptAudio?.pause();
        audioState.responseWaveSurfer?.stop();
        if (recording) stopRecording();
        updateAudioState(as => ({
            ...as,
            promptPlaying: false,
            responsePlaying: false,
            recording: false,
        }));
    };

    const play = (id: string) => {
        stopEverything();
        updateAudioState(as => ({
            ...as,
            promptPlaying: id === promptId,
            responsePlaying: id === "waveform" + props.questionIdx,
        }));

        if (id === promptId) getPromptAudio(props.questionIdx)?.play();
        else audioState.responseWaveSurfer?.play();
    };

    const record = () => {
        stopEverything();
        playStartTone();
        updateAudioState(as => ({ ...as, recording: true }));
        startRecording();
    };

    // add event listener for when prompt audio ceases
    useEffect(() => {
        getPromptAudio(props.questionIdx)?.addEventListener("ended", _ => {
            updateAudioState(as => ({ ...as, promptPlaying: false }));
        });
    }, []);

    // stop wavesurfer when component unmounts ---------------------------------
    // wavesurfers should stop automatically as station swipes due to (unmount on exit)
    // this will also reset audiostate.responseplaying
    useEffect(() => () => audioState.responseWaveSurfer?.stop(), [audioState.responseWaveSurfer]);
    // -------------------------------------------------------------------------

    return (
        <>
            <div
                style={{
                    gridRowStart: 2,
                    gridColumnStart: 1,
                    height: "60vh",
                    transition: `transform ${500}ms`,
                    ...props.transitionStyle,
                }}
            >
                <QuestionPrompt
                    question={props.question}
                    toggle={() => {
                        promptPlaying ? stopEverything() : play(promptId);
                    }}
                    promptId={promptId + props.questionIdx}
                />
                <Box
                    style={{
                        display: "flex",
                        alignItems: "center",
                        height: "49vh",
                    }}
                >
                    <Img src={props.question.img} narrowWindow={narrowWindow}></Img>
                    <br />
                </Box>
            </div>
            <div
                style={{
                    gridRowStart: 4,
                    gridColumnStart: 1,
                    zIndex: 700,
                    backgroundColor: "#1c2124",
                    height: "15vh",
                }}
            >
                <WaveSurfer
                    playing={responsePlaying}
                    playResponse={() => play("waveform" + props.questionIdx)}
                    stopEverything={stopEverything}
                    responseAudio={props.question.responseAudio}
                    updateAudioState={updateAudioState}
                    questionIdx={props.questionIdx}
                />

                <RecordControl
                    recording={recording}
                    recordResponse={record}
                    stopEverything={stopEverything}
                />
            </div>
        </>
    );
};

export default QuestionContent;

const Img = styled.img<{ narrowWindow: boolean }>`
    display: block;
    margin: auto;
    max-width: 100%;
    max-height: 100%;
    transition: width 0.2s;
`;
