import { Dispatch, SetStateAction, useContext, useEffect, useState } from "react";
import { FaPause, FaPlay } from "react-icons/fa";
import styled from "styled-components";
import MetaContext from "../../../MetaContext";
import { Box } from "../Common";
import { generateWaveSurfer } from "../hooks";
import { AudioState } from "./QuestionReviewContent";

type WaveSurferReviewProps = {
    playing: boolean;
    play: () => void;
    stopEverything: () => void;
    responseAudio: string;
    exemplarAudio: string;
    updateAudioState: Dispatch<SetStateAction<AudioState>>;
    questionIdx: number;
};

const WaveSurferReview = (props: WaveSurferReviewProps) => {
    const [selection, updateSelection] = useState<"response" | "exemplar">("response");
    const { narrowWindow } = useContext(MetaContext);

    const handleClick = (sel: "response" | "exemplar"): void => {
        // if already selected then just toggle
        if (sel === selection) return props.playing ? props.stopEverything() : props.play();

        const container = document.getElementById("waveform" + props.questionIdx);
        if (container) container.innerHTML = "";

        const audio = sel === "response" ? props.responseAudio : props.exemplarAudio;
        const waveSurfer = generateWaveSurfer("waveform" + props.questionIdx, audio);
        waveSurfer.on("ready", _ => waveSurfer.play());
        waveSurfer.on("finish", _ => {
            console.log("finished");
            props.updateAudioState(as => ({
                ...as,
                responsePlaying: false,
            }));
        });

        props.updateAudioState(as => ({
            ...as,
            waveSurfer,
            playing: waveSurfer.isPlaying(),
        }));

        updateSelection(sel);
    };

    useEffect(() => {
        props.updateAudioState(as => ({
            ...as,
            waveSurfer: generateWaveSurfer("waveform" + props.questionIdx, props.responseAudio),
        }));
    }, []);

    return (
        <div
            style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginTop: "10px",
            }}
        >
            <AudioSelect
                onClick={() => handleClick("response")}
                narrowWindow={narrowWindow}
                selected={selection === "response"}
            >
                {selection === "response" && props.playing ? <FaPause /> : <FaPlay />}
                &nbsp; You
            </AudioSelect>

            <AudioSelect
                onClick={() => handleClick("exemplar")}
                narrowWindow={narrowWindow}
                selected={selection === "exemplar"}
            >
                {selection === "exemplar" && props.playing ? <FaPause /> : <FaPlay />}
                &nbsp; Exemplar
            </AudioSelect>

            <Box style={{ margin: "0", width: "87%", borderRadius: "5px", height: "30px" }}>
                <div id={"waveform" + props.questionIdx}></div>
            </Box>
        </div>
    );
};

export default WaveSurferReview;

const AudioSelect = styled.button<{ selected: boolean; narrowWindow: boolean }>`
    background-color: ${props => (props.selected ? "green" : "#414142")};
    border-radius: 5px;
    margin-right: 5px;
    height: 30px;
    align-content: center;
    width: 130px;
    border: 1px solid #414142;
    color: ${props => (props.selected ? "white" : "#FAD000")};
    font-size: ${props => (props.narrowWindow ? "12px" : "15px")};
`;
