import { useContext } from "react";
import { FaMicrophone, FaPause, FaPlay } from "react-icons/fa";
import styled from "styled-components";
import MetaContext from "../../MetaContext";

type TaskSectionReviewProps = {
    label: string;
    playing: boolean;
    play: () => void;
    stop: () => void;
};

const TaskSectionReview = (props: TaskSectionReviewProps) => {
    const { narrowWindow } = useContext(MetaContext);
    return (
        <PlaybackControl
            busy={props.playing}
            narrowWindow={narrowWindow}
            onClick={props.playing ? props.stop : props.play}
        >
            {props.label}
            <ToggleButton busy={props.playing}>
                {props.playing ? <FaPause /> : <FaPlay />}
            </ToggleButton>
        </PlaybackControl>
    );
};

export default TaskSectionReview;

const PlaybackControl = styled.button<{ busy: boolean; narrowWindow: boolean }>`
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #414142;
    border-radius: 5px;
    height: 35px;
    align-content: center;
    margin: auto;
    width: 100%;
    border: 1px solid ${props => (props.busy ? "red" : "#414142")};
    color: ${props => (props.busy ? "red" : "#FAD000")};
    font-size: ${props => (props.narrowWindow ? "12px" : "15px")};
`;

const ToggleButton = styled.button<{ busy: boolean }>`
    height: 35px;
    width: 35px;
    border-style: none;
    color: ${props => (props.busy ? "red" : "#FAD000")};
    margin: 10px;
    background-color: transparent;
`;
