import { FaVolumeUp } from "react-icons/fa"
import styled from "styled-components"
import { promptId, Question } from "./types"
import heart from "./media/heart.png"

type QuestionPromptProps = {
    question: Question
    toggle: () => void
}

const QuestionPrompt = (props: QuestionPromptProps) => {

    return (
        <div style = {{ }}>

            <div style = {{ display: "inline-block", margin: "20px 0" }}><img src = { heart } width = "100px"/></div>

            <Prompt onClick = { props.toggle }>

                <FaVolumeUp /> &nbsp; &nbsp; { props.question.question }

                <audio autoPlay = {true} src = { props.question.questionUrl } id = { promptId }/>

            </Prompt>

        </div>
    )
}

export default QuestionPrompt

const Prompt = styled.div`
    display: inline-block;
    color: #FAD000;
    border: 1px solid grey;
    border-radius: 5px;
    margin: 0 20px; 
    padding: 5px;
    &:hover {
        cursor: pointer;
        border: 2px solid #FAD000;
    }
`
