import { useState } from "react";
import styled from "styled-components";
import { questions } from "./exampleQuestions";

type QuestionSelectorProps = {};

const QuestionSelector = (props: QuestionSelectorProps) => {
    const [selectedQuestion, updateSelectedQuestion] = useState();
    const [selectedFinding, updateSelectedFinding] = useState();
    const [selectedCategory, updateSelectedCategory] = useState();

    // load these using use effect and initialise selection
    // for now using local dummy questions for UI testing
    // useEffect( sommin => quesitons! )

    const categories = Array(...new Set(questions.map(q => q.category)));

    const filteredQuestions = questions.filter(q => q.category === c && q.finding === f);

    return (
        <Container>
            <div style={{ width: "15%", backgroundColor: "#1c2124" }}>
                {categories.map(c => {
                    const findings = Array(
                        ...new Set(questions.filter(q => q.category === c).map(q => q.finding))
                    );

                    return (
                        <div style={{ backgroundColor: "#1c2124" }}>
                            <CategoryTitle>{c}</CategoryTitle>
                            {findings.map(f => (
                                <FindingOption
                                    onClick={() => {
                                        updateSelectedFinding(f);
                                        updateSelectedCategory(c);
                                    }}
                                >
                                    {f}
                                </FindingOption>
                            ))}
                        </div>
                    );
                })}
            </div>
            <div></div>
        </Container>
    );
};

export default QuestionSelector;

const Container = styled.div`
    background-color: #2f3237;
    margin: 5%;
    padding: 5%;
    display: flex;
`;

const FindingOption = styled.div`
    background-color: #2f3237;
    margin: 5%;
    padding: 5%;
    display: flex;
    cursor: pointer;
`;

const CategoryTitle = styled.div`
    font-size: 20px;
`;
