import { Dispatch, SetStateAction, useEffect } from "react";
import { Redirect } from "react-router-dom";
import { Timer } from "./Common";
import { useLoopTime } from "./hooks";
import TimerBar from "./TimerBar";
import { Case, Station, Station_new } from "./types";

type TimerApparatusProps = {
    station: Station_new;
    questionIdx: number;
    updateStation: Dispatch<SetStateAction<Station_new>>
}

const TimerApparatus = (props: TimerApparatusProps) => {

    const pathToNext = props.questionIdx === props.station.questions.length - 1 ? "/Demo/Summary" : "/Demo/" + (props.questionIdx + 1);
    const secondTicker = useLoopTime(props.station.timeRemaining - 1);  

    useEffect(() => () => {
        props.updateStation(s => { 
            return { ...s, timeRemaining: secondTicker }
        })
    }, [secondTicker])

    if (secondTicker <= 0) return <Redirect to = { pathToNext } />;

    return (
        <div style = {{ textAlign: "center" }}>
            <Timer> 00 : { secondTicker < 10 ? "0" : "" }{ secondTicker } </Timer>
        </div>
    )
}

export default TimerApparatus;;
