import axios from "../../myaxios";
import { Dispatch, ReactElement, SetStateAction, useContext, useState } from "react";
import styled from "styled-components";
import MetaContext from "../../MetaContext";
import { Login, LoginButton } from "../HomePage/LoginLogout";
import { StartButton } from "./Common";
import { NullStation } from "./DemoStations/NullStation";
import { binaryToCode, countPoints } from "./hooks";
import TimerApparatus from "./TimerApparatus";
import TimerBar from "./TimerBar";
import { Question, Station } from "./types";
import htmlParser from "react-html-parser";
import { Link } from "react-router-dom";

type HeaderProps = {
    station: Station;
    questionIdx?: number;
    updateStation: Dispatch<SetStateAction<Station>>;
    timedHeader: boolean;
    percentage: number;
    briefPrompt: string;
    title: string;
    handleExit: (questions: Question[], tok: string) => void;
};

const Header = (props: HeaderProps) => {
    const { token, SERVER_URL } = useContext(MetaContext);
    const [briefShowing, updateBriefShowing] = useState(false);
    const [displayLoginOption, updateDisplayLoginOption] = useState(false);

    return (
        <HeaderContainer>
            <div style={{ display: "flex", justifyContent: "space-between" }}>
                <div>
                    <h2>{props.title}</h2>
                </div>
                {props.timedHeader ? (
                    <TimerApparatus
                        station={props.station!}
                        questionIdx={props.questionIdx!}
                        updateStation={props.updateStation!}
                    />
                ) : (
                    <></>
                )}
            </div>

            <TimerBar percentage={props.percentage} />

            <br />

            <div style={{ display: "flex", justifyContent: "space-between" }}>
                <div>
                    <StartButton onClick={() => updateBriefShowing(!briefShowing)}>
                        {briefShowing ? "Hide" : "Show"} Brief
                    </StartButton>
                </div>

                {token ? (
                    <StartButton onClick={() => props.handleExit(props.station?.questions, token)}>
                        Exit Demo
                    </StartButton>
                ) : (
                    <StartButton onClick={() => updateDisplayLoginOption(true)}>
                        Exit Demo
                    </StartButton>
                )}
            </div>

            <br />

            {displayLoginOption ? (
                <div style={{ textAlign: "center" }}>
                    <Login
                        label={"Login & Save!"}
                        successCallback={t => props.handleExit(props.station?.questions, t)}
                    />
                    <Link to="/OSCE">
                        <LoginButton>Exit without saving.</LoginButton>
                    </Link>
                </div>
            ) : (
                <></>
            )}

            {briefShowing ? htmlParser(props.briefPrompt) : <></>}
        </HeaderContainer>
    );
};

export default Header;

const HeaderContainer = styled.div`
    position: sticky;
    top: 0;
    max-width: 800px;
    background-color: #1c2124;
    padding-top: 50px;
    opacity: 0.98;
    z-index: 999;
`;
