import React, { Dispatch, SetStateAction, useContext } from "react";
import styled from "styled-components";
import MetaContext from "../../MetaContext";
import { StationSummary } from "./types";

type CategorySelectorProps = {
    updateCategory: Dispatch<SetStateAction<string>>;
    stationSummaries: StationSummary[];
    category: string;
};

const CategorySelector = (props: CategorySelectorProps) => {
    const { narrowWindow } = useContext(MetaContext);

    return narrowWindow ? (
        <div>
            <Select onChange={e => props.updateCategory(e.target.value)}>
                {["All"]
                    .concat([...new Set(props.stationSummaries.map(ss => ss.category))])
                    .map((c, i) => (
                        <option key={i}>{c}</option>
                    ))}
            </Select>
        </div>
    ) : (
        <div>
            {[...new Set(props.stationSummaries.map(ss => ss.category))].map((c, i) => (
                <Category
                    selected={c === props.category}
                    onClick={() => props.updateCategory(c)}
                    key={i}
                >
                    {c}
                </Category>
            ))}
        </div>
    );
};

export default CategorySelector;

const Select = styled.select`
    margin-bottom: 20px;
    background-color: #1c2124;
    border-radius: 5px;
    color: white;
    border-style: none;
    padding: 5px;
    text-align: center;
`;

const Category = styled.div<{ selected: boolean }>`
    margin-bottom: 20px;
    background-color: ${props => (props.selected ? "#1c2124" : "transparent")};
    border-radius: 5px;
    color: white;
    border-style: none;
    padding: 5px;
    text-align: center;
    cursor: pointer;
`;
