import { Dispatch, SetStateAction } from "react";
import { useHistory } from "react-router-dom";
import { HeaderDetails } from "./types";
import { History, LocationState } from "history";

export const headerDetailSwitch = (
    lastPath: string,
    questionCount: number,
    questionIdx: number,
    updateQuestionIdx: Dispatch<SetStateAction<number>>,
    history: History<LocationState>
): HeaderDetails => {
    if (lastPath === "Brief")
        return {
            title: "Brief",
            percentage: 5,
            briefAndExit: false,
            timer: false,
            nextFunc: () => history.push("/OSCE/Station"),
            prevFunc: () => history.push("/OSCE/Station"),
        };
    else if (lastPath === "Question") {
        return {
            title: "Station One",
            percentage: 10 + (100 * questionIdx) / questionCount,
            briefAndExit: true,
            timer: true,
            nextFunc: () =>
                questionIdx === questionCount - 1
                    ? history.push("/OSCE/Station/Summary")
                    : updateQuestionIdx(questionIdx + 1),
            prevFunc: () => (questionIdx === 0 ? {} : updateQuestionIdx(questionIdx + 1)),
        };
    } else if (lastPath === "Summary")
        return {
            title: "Finish",
            percentage: 100,
            briefAndExit: true,
            timer: false,
            nextFunc: () => {
                updateQuestionIdx(0);
                history.push("/OSCE/Station/Review");
            },
            prevFunc: () => {},
        };
    else if (lastPath === "Review") {
        return {
            title: "Review",
            percentage: 10 + (100 * questionIdx) / questionCount,
            briefAndExit: true,
            timer: false,
            nextFunc: () =>
                questionIdx === questionCount - 1
                    ? history.push("/OSCE/Station/MarkReview")
                    : updateQuestionIdx(questionIdx + 1),
            prevFunc: () => (questionIdx === 0 ? {} : updateQuestionIdx(questionIdx - 1)),
        };
    } else
        return {
            title: "Summary",
            percentage: 100,
            briefAndExit: true,
            timer: false,
            nextFunc: () => {},
            prevFunc: () => {
                updateQuestionIdx(questionCount - 1);
                history.push("/OSCE/Station/Review");
            },
        };
};
