import React, { Dispatch, SetStateAction, useContext } from "react";
import styled from "styled-components";
import MetaContext from "../../MetaContext";
import { StationSummary } from "./types";

type FilterSelectorProps = {
    updateFilter: Dispatch<SetStateAction<Filter>>;
    stationSummaries: StationSummary[];
    filter: Filter;
};

export type Filter = "All" | "Attempted" | "Free" | "Premium";

const FilterSelector = (props: FilterSelectorProps) => {
    const { narrowWindow } = useContext(MetaContext);

    return narrowWindow ? (
        <div>
            <Select onChange={e => props.updateFilter(e.target.value as Filter)}>
                {["All", "Attempted", "Free", "Premium"].map((c, i) => (
                    <option key={i}>{c}</option>
                ))}
            </Select>
        </div>
    ) : (
        <FilterSelectorBar>
            {(["Attempted", "Free", "Premium"] as Filter[]).map((f, i) => (
                <Category
                    selected={f === props.filter}
                    onClick={() => props.updateFilter(f === props.filter ? "All" : f)}
                    key={i}
                >
                    {f}
                </Category>
            ))}
        </FilterSelectorBar>
    );
};

export default FilterSelector;

const Select = styled.select`
    margin-bottom: 20px;
    background-color: #1c2124;
    border-radius: 5px;
    color: white;
    border-style: none;
    padding: 5px;
    text-align: center;
`;

const Category = styled.div<{ selected: boolean }>`
    margin-right: 20px;
    background-color: ${props => (props.selected ? "#1c2124" : "transparent")};
    border-radius: 5px;
    color: white;
    border-style: none;
    padding: 5px;
    cursor: pointer;
`;

const FilterSelectorBar = styled.div`
    display: flex;
    margin-bottom: 20px;
    padding-bottom: 10px;
    border-bottom: 1px solid grey;
    border-radius: 0;
`;
