import { FaVolumeUp } from "react-icons/fa";
import styled from "styled-components";
import { promptId, Question } from "./types";
import heart from "./media/heart.png";
import { useContext } from "react";
import MetaContext from "../../MetaContext";
import { useWindowSize } from "../../functions";

type QuestionPromptProps = {
    question: Question;
    toggle: () => void;
    promptId: string;
};

const QuestionPrompt = (props: QuestionPromptProps) => {
    const { narrowWindow } = useContext(MetaContext);

    return (
        <div
            style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                backgroundColor: "#414142",
                borderRadius: "10px",
                margin: "auto",
                border: "5px solid transparent",
                width: "max-content",
            }}
        >
            <div style={{ margin: "20px 0" }}>
                <img src={heart} width={narrowWindow ? "35px" : "75px"} />
            </div>
            <div>
                <Prompt onClick={props.toggle}>
                    <FaVolumeUp />
                    <span style={{ fontSize: narrowWindow ? "12px" : "15px" }}>
                        &nbsp; &nbsp; {props.question.question}
                    </span>
                    <audio src={props.question.questionAudio} id={props.promptId} autoPlay />
                </Prompt>
            </div>
        </div>
    );
};

export default QuestionPrompt;

const Prompt = styled.div`
    display: flex;
    align-items: center;
    color: #fad000;
    padding: 5px;
    &:hover {
        cursor: pointer;
    }
`;
