import React, { Dispatch, SetStateAction } from "react";
import styled from "styled-components";
import { StationSummary } from "./types";

type CategorySelectorProps = {
    updateCategory: Dispatch<SetStateAction<string>>;
    stationSummaries: StationSummary[];
    category: string;
};

const CategorySelector = (props: CategorySelectorProps) => {
    const handleSelection = (sel: string) => {
        props.updateCategory(sel);
    };

    return (
        <Container>
            {["All"].concat([...new Set(props.stationSummaries.map(ss => ss.category))]).map(c => (
                <CategoryOption onSelect={() => handleSelection(c)}>{c}</CategoryOption>
            ))}
        </Container>
    );
};

export default CategorySelector;

const Container = styled.select`
    display: flex;
    margin-bottom: 20px;
    overflow: scroll;
    -ms-overflow-style: none; /* for Internet Explorer, Edge */
    scrollbar-width: none; /* for Firefox */
    overflow-y: scroll;
    &::-webkit-scrollbar {
        display: none; /* for Chrome, Safari, and Opera */
    }
`;

const CategoryOption = styled.option``;
