import { Dispatch, SetStateAction, useEffect, useState } from "react";
import { useReactMediaRecorder } from "react-media-recorder";
import styled from "styled-components";
import { copy } from "../ECGPage/Functions/utility";
import { Box } from "./Common";
import { generateWaveSurfer, playStartTone } from "./hooks";
import QuestionPrompt from "./QuestionPrompt";
import TaskSection from "./TaskSection";
import { promptId, Question, responseId, Station } from "./types";

type QuestionContentProps = {
    question: Question;
    lastQuestion: boolean;
    updateStation: Dispatch<SetStateAction<Station>>;
    questionIdx: number;
    updateQuestionIdx: Dispatch<SetStateAction<{ prev: number; curr: number }>>;
    transitionStyle: any;
};

export type AudioState = {
    promptAudio: HTMLAudioElement | null;
    responseWaveSurfer: WaveSurfer | undefined;
    promptPlaying: boolean;
    responsePlaying: boolean;
    recording: boolean;
};

const initialAudioState: AudioState = {
    promptAudio: null,
    responseWaveSurfer: undefined,
    promptPlaying: true,
    responsePlaying: false,
    recording: false,
};

const QuestionContent = (props: QuestionContentProps) => {
    const [audioState, updateAudioState] = useState<AudioState>(initialAudioState);
    const [loadingRecorder, updateLoadingRecorder] = useState(false);

    const { startRecording, stopRecording } = useReactMediaRecorder({
        audio: true,
        onStart: () => {
            updateLoadingRecorder(false);
            updateAudioState(as => ({ ...as, recording: true }));
            playStartTone();
        },
        onStop: (blobUrl, _) => {
            playStartTone();
            props.updateStation(s => {
                s.questions[props.questionIdx].responseAudio = blobUrl ? blobUrl : "";
                return copy(s); // because object is nested, react shallow compares
            });
            if (!props.lastQuestion) {
                props.updateQuestionIdx(qi => ({
                    prev: qi.curr,
                    curr: props.questionIdx + 1,
                }));
            }
        },
    });

    const stopEverything = () => {
        console.log("stop everything");
        audioState.promptAudio?.pause();
        audioState.responseWaveSurfer?.stop();
        if (audioState.recording) stopRecording();
        updateAudioState(as => ({
            ...as,
            promptPlaying: false,
            responsePlaying: false,
            recording: false,
        }));
    };

    const play = (id: string) => {
        stopEverything();
        updateAudioState(as => ({
            ...as,
            promptPlaying: id === promptId,
            responsePlaying: id === responseId,
        }));

        if (id === promptId) audioState.promptAudio?.play();
        else audioState.responseWaveSurfer?.play();
    };

    const record = () => {
        stopEverything();
        updateLoadingRecorder(true);
        startRecording();
    };

    return (
        <>
            <div
                style={{
                    gridRowStart: 1,
                    gridColumnStart: 1,
                    transition: `transform ${500}ms`,
                    ...props.transitionStyle,
                }}
            >
                <Box style={{ textAlign: "center" }}>
                    <QuestionPrompt
                        question={props.question}
                        toggle={() => {
                            audioState.promptPlaying ? stopEverything() : play(promptId);
                        }}
                    />

                    <br></br>

                    <Img src={props.question.img}></Img>
                    <br />
                </Box>
            </div>
            <div
                style={{
                    gridRowStart: 2,
                    gridColumnStart: 1,
                    zIndex: 1000,
                    backgroundColor: "#1c2124",
                    height: "100vh",
                }}
            >
                <TaskSection
                    recording={audioState.recording}
                    recordResponse={record}
                    playing={audioState.responsePlaying}
                    playResponse={() => play(responseId)}
                    stopEverything={stopEverything}
                    responseAudio={props.question.responseAudio}
                    loadingRecorder={loadingRecorder}
                    updateAudioState={updateAudioState}
                />
            </div>
        </>
    );
};

export default QuestionContent;

const Img = styled.img`
    display: block;
    margin: auto;
    width: 60%;
    transition: width 0.2s;
`;
