import { Dispatch, SetStateAction } from "react";
import { HeaderDetails } from "./types";

export const headerDetailSwitch = (
    lastPath: string,
    questionCount: number,
    questionIdx: number,
    updateQuestionIdx: Dispatch<SetStateAction<number>>
): HeaderDetails => {
    if (lastPath === "Brief")
        return {
            title: "Brief",
            percentage: 5,
            briefAndExit: false,
            timer: false,
            nextFunc: () => (window.location.href = "/OSCE/Station"),
            prevFunc: () => (window.location.href = "/OSCE/Station"),
        };
    else if (lastPath === "Question") {
        return {
            title: "Station One",
            percentage: 10 + (100 * questionIdx) / questionCount,
            briefAndExit: true,
            timer: true,
            nextFunc: () =>
                questionIdx === questionCount - 1
                    ? (window.location.href = "/OSCE/Station/Summary")
                    : updateQuestionIdx(questionIdx + 1),
            prevFunc: () => (questionIdx === 0 ? {} : updateQuestionIdx(questionIdx + 1)),
        };
    } else if (lastPath === "Summary")
        return {
            title: "Finish",
            percentage: 100,
            briefAndExit: true,
            timer: false,
            nextFunc: () => {
                updateQuestionIdx(0);
                window.location.href = "/OSCE/Station/Review";
            },
            pathToPrev: "/OSCE",
        };
    else if (lastPath.startsWith("Review")) {
        const i = Number(lastPath.substring(lastPath.indexOf("w") + 1));
        const percentage = 10 + (100 * i) / questionCount;
        return {
            title: "Review",
            percentage,
            briefAndExit: true,
            timer: false,
            pathToNext:
                "/OSCE/Station/" + (i === questionCount - 1 ? "MarkReview" : "Review" + (i + 1)),
            pathToPrev: "/OSCE/Station/" + (i === 0 ? "Summary" : "Review" + (i - 1)),
        };
    } else
        return {
            title: "Summary",
            percentage: 100,
            briefAndExit: true,
            timer: false,
            pathToNext: "/OSCE/Station/MarkReview",
            pathToPrev: "/OSCE/Station/Review" + (questionCount - 1),
        };
};
