import React, { Dispatch, SetStateAction } from "react";
import styled from "styled-components";
import { StationSummary } from "./types";

type CategorySelectorProps = {
    updateCategory: Dispatch<SetStateAction<string>>;
    stationSummaries: StationSummary[];
    category: string;
};

const CategorySelector = (props: CategorySelectorProps) => {
    return (
        <Select onChange={e => props.updateCategory(e.target.value)}>
            {["All"].concat([...new Set(props.stationSummaries.map(ss => ss.category))]).map(c => (
                <option>{c}</option>
            ))}
        </Select>
    );
};

export default CategorySelector;

const Select = styled.select`
    margin-bottom: 20px;
    background-color: #1c2124;
    border-radius: 5px;
    color: white;
    border-style: none;
    padding: 5px;
    text-align: center;
`;
