import { Dispatch, SetStateAction, useContext, useEffect, useRef, useState } from "react";
import {
    ReactMediaRecorderRenderProps,
    StatusMessages,
    useReactMediaRecorder,
} from "react-media-recorder";
import styled from "styled-components";
import { copy } from "../../../old-components/ECGPage/Functions/utility";
import { Box } from "../Common";
import { generateWaveSurfer, playStartTone, populateResponseAudios } from "../hooks";
import QuestionPrompt from "../QuestionPrompt";
import RecordControl from "./RecordControl";
import WaveSurfer from "./WaveSurfer";
import { promptId, Question, responseId, Station } from "../types";
import MetaContext from "../../../MetaContext";
import startToneUrl from "../media/start.mp3";
import { produceHeat } from "../../../functions";

type QuestionContentProps = {
    question: Question;
    updateStation: Dispatch<SetStateAction<Station>>;
    active: boolean;
    questionIdx: number;
    updateQuestionIdx: Dispatch<SetStateAction<{ prev: number; curr: number }>>;
    nextFunc: () => void;
};

// lets implement things as a SEQUENCE?!!!!

const QuestionContent = (props: QuestionContentProps) => {
    const { narrowWindow } = useContext(MetaContext);

    const { startRecording, stopRecording, status } = useReactMediaRecorder({
        audio: true,
        onStop: (blobUrl, _) => {
            console.log("recording stopped");
            props.updateStation(s => {
                s.questions[props.questionIdx].responseAudio = blobUrl ? blobUrl : "";
                return copy(s); // because object is nested, react shallow compares
            });

            const endTone = document.getElementById("endTone") as HTMLAudioElement;
            endTone.play();
        },
    });

    // stop wavesurfer when component unmounts ---------------------------------
    // wavesurfers should stop automatically as station swipes due to (unmount on exit)
    // this will also reset responseplaying
    // useEffect(() => {
    //     return () => {
    //         console.log(props.questionIdx, "unmounted");
    //         responseWaveSurfer?.stop();
    //     };
    // });
    // -------------------------------------------------------------------------

    // this is set to true in the same render cycle as station update to ensure
    // unmounting occurs only after succesfful update---------------------------
    const [shouldProgress, updateShouldProgress] = useState(false);
    if (shouldProgress) {
        updateShouldProgress(false);
        props.nextFunc();
    }
    // -------------------------------------------------------------------------

    const recording = status === "recording";

    const promptAudio = document.getElementById(
        promptId + props.questionIdx
    ) as HTMLAudioElement | null;

    const [responseWaveSurfer, updateResponseWaveSurfer] = useState<WaveSurfer | undefined>(
        undefined
    );

    const responsePlaying = responseWaveSurfer ? responseWaveSurfer.isPlaying() : false;

    const stopEverything = () => {
        promptAudio?.pause();
        responseWaveSurfer?.stop();
        if (recording) stopRecording();
    };

    const play = (id: string) => {
        stopEverything();
        if (id === promptId) promptAudio?.play();
        else responseWaveSurfer?.play();
    };

    useEffect(() => {
        if (props.active) {
            console.log("PLAAAAY");
            promptAudio?.play();
        } else stopEverything();
    }, [props.active]);

    return (
        <div>
            <div
                style={{
                    gridRowStart: 2,
                    gridColumnStart: 1,
                    height: "60vh",
                    transition: `transform ${500}ms`,
                }}
            >
                <QuestionPrompt
                    question={props.question}
                    toggle={() => {
                        !promptAudio?.paused ? stopEverything() : play(promptId);
                    }}
                    promptId={promptId + props.questionIdx}
                />
                <Box
                    style={{
                        display: "flex",
                        alignItems: "center",
                        height: "49vh",
                    }}
                >
                    <Img src={props.question.img} narrowWindow={narrowWindow}></Img>
                    <br />
                </Box>
            </div>
            <div
                style={{
                    gridRowStart: 4,
                    gridColumnStart: 1,
                    zIndex: 700,
                    backgroundColor: "#1c2124",
                    height: "15vh",
                }}
            >
                <WaveSurfer
                    playing={responsePlaying}
                    playResponse={() => play("waveform" + props.questionIdx)}
                    stopEverything={stopEverything}
                    responseAudio={props.question.responseAudio}
                    questionIdx={props.questionIdx}
                    updateResponseWaveSurfer={updateResponseWaveSurfer}
                />

                <RecordControl
                    recording={recording}
                    stopEverything={stopEverything}
                    startRecording={startRecording}
                />

                <audio id="endTone" src={startToneUrl} onEnded={() => updateShouldProgress(true)} />
            </div>
        </div>
    );
};

export default QuestionContent;

const Img = styled.img<{ narrowWindow: boolean }>`
    display: block;
    margin: auto;
    max-width: 100%;
    max-height: 100%;
    transition: width 0.2s;
`;
