import { Dispatch, SetStateAction, useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { StartButton, Box, MoveOn, Container } from "./Common";
import TaskSection from "./TaskSection";
import {
    exemplarId,
    promptId,
    Question,
    responseId,
    startAudioRecordingId,
    Station,
} from "./types";
import QuestionPrompt from "./QuestionPrompt";
import { generateWaveSurfer, playStartTone, useRecorder } from "./hooks";
import Header from "./Header";
import recordingStartAudioUrl from "./media/start.mp3";
import MetaContext from "../../MetaContext";
import { produceHeat } from "../../functions";
import { useReactMediaRecorder } from "react-media-recorder";

type QuestionProps = {
    station: Station;
    questionIdx: number;
    updateStation: Dispatch<SetStateAction<Station>>;
    handleExit: (questions: Question[], tok: string) => void;
};

type AudioState = {
    promptAudio: HTMLAudioElement | null;
    responseWaveSurfer: WaveSurfer | undefined;
    promptPlaying: boolean;
    responsePlaying: boolean;
    recording: boolean;
};

const initialAudioState: AudioState = {
    promptAudio: null,
    responseWaveSurfer: undefined,
    promptPlaying: false,
    responsePlaying: false,
    recording: false,
};

const QuestionPage = (props: QuestionProps) => {
    const [imageExpanded, updateImageExpanded] = useState(false);
    const [audioState, updateAudioState] = useState<AudioState>(initialAudioState);
    const [loadingRecorder, updateLoadingRecorder] = useState(false);

    const { startRecording, stopRecording, mediaBlobUrl } = useReactMediaRecorder({
        audio: true,
        onStart: playStartTone,
        onStop: () => {
            playStartTone();
            updateLoadingRecorder(false);
        },
    });

    useEffect(() => {
        console.log("mediabloburl", mediaBlobUrl);
        props.updateStation(s => {
            s.questions[props.questionIdx].responseAudio = mediaBlobUrl ? mediaBlobUrl : "";
            return s;
        });
    }, [mediaBlobUrl]);

    const percentage = 10 + (100 * props.questionIdx) / props.station.questions.length;
    const pathToNext =
        props.questionIdx === props.station.questions.length - 1
            ? "/OSCE/Station/Summary"
            : "/OSCE/Station/" + (props.questionIdx + 1);
    const question = props.station.questions[props.questionIdx];
    const { SERVER_URL } = useContext(MetaContext);

    const stopEverything = () => {
        audioState.promptAudio?.pause();
        audioState.responseWaveSurfer?.stop();
        if (audioState.recording) stopRecording();
        updateAudioState(as => ({
            ...as,
            promptPlaying: false,
            responsePlaying: false,
            recording: false,
        }));
    };

    const play = (id: string) => {
        stopEverything();
        updateAudioState(as => ({
            ...as,
            promptPlaying: id === promptId,
            responsePlaying: id === responseId,
        }));

        if (id === promptId) audioState.promptAudio?.play();
        else audioState.responseWaveSurfer?.play();
    };

    const record = () => {
        stopEverything();
        // updateLoadingRecorder(true);
        updateAudioState(as => ({ ...as, recording: true }));
        startRecording();
    };

    useEffect(() => {
        const container = document.getElementById(responseId);
        if (container) container.innerHTML = "";

        updateAudioState({
            ...initialAudioState,
            promptAudio: document.getElementById(promptId) as HTMLAudioElement,
            responseWaveSurfer: generateWaveSurfer(
                responseId,
                question.responseAudio,
                stopEverything
            ),
        });
    }, [question.responseAudio]);

    // cease playback when compnent unmounts <=== six hour bug :)
    useEffect(() => stopEverything, [audioState.promptAudio, audioState.responseWaveSurfer]);

    return (
        <Container>
            <Header
                station={props.station}
                questionIdx={props.questionIdx}
                updateStation={props.updateStation}
                timedHeader={true}
                percentage={percentage}
                briefPrompt={props.station.brief.prompt}
                title={"Station One"}
                handleExit={props.handleExit}
            />

            <br />

            <Box style={{ textAlign: "center" }}>
                <QuestionPrompt
                    question={question}
                    toggle={() => {
                        audioState.promptPlaying ? stopEverything() : play(promptId);
                    }}
                />

                <br></br>

                <div style={{ textAlign: "center" }}>
                    <Img
                        src={question.img}
                        imageExpanded={imageExpanded}
                        onClick={() => updateImageExpanded(!imageExpanded)}
                    ></Img>
                    Click to {imageExpanded ? "shrink" : "expand"}!
                </div>

                <br></br>

                <TaskSection
                    recording={audioState.recording}
                    recordResponse={record}
                    playing={audioState.responsePlaying}
                    playResponse={() => play(responseId)}
                    stopEverything={stopEverything}
                    responseAvailable={question.responseAudio !== ""}
                    loadingRecorder={loadingRecorder}
                />

                <br></br>

                {question.responseAudio !== "" || audioState.recording ? (
                    <Link to={pathToNext} onClick={stopEverything}>
                        <MoveOn>Move On</MoveOn>
                    </Link>
                ) : (
                    <></>
                )}
            </Box>

            <audio src={recordingStartAudioUrl} id={startAudioRecordingId} />
        </Container>
    );
};

export default QuestionPage;

const Img = styled.img<{ imageExpanded: boolean }>`
    display: block;
    margin: auto;
    width: ${props => (props.imageExpanded ? "100%" : "60%")};
    transition: width 0.2s;
    &:hover {
        cursor: pointer;
    }
`;
