import { Dispatch, SetStateAction } from "react";
import { Container } from "../Common";
import { Question, Station } from "../types";
import { Transition } from "react-transition-group";
import QuestionReviewContent from "./QuestionReviewContent";
import { useHistory } from "react-router-dom";
import Header from "../Header";

type QuestionReviewPageProps = {
    station: Station;
    updateStation: Dispatch<SetStateAction<Station>>;
    questionIdx: { prev: number; curr: number };
    updateIdx: Dispatch<SetStateAction<{ prev: number; curr: number }>>;
    reviewMode: boolean;
    handleExit: (questions: Question[], tok: string) => void;
};

const QuestionReviewPage = (props: QuestionReviewPageProps) => {
    const history = useHistory();
    const idx = props.questionIdx;
    const questionCount = props.station.questions.length;

    const nextFunc = () =>
        idx.curr === questionCount - 1
            ? history.push("/OSCE/Station/MarkReview")
            : props.updateIdx({ prev: idx.curr, curr: idx.curr + 1 });

    const prevFunc = () =>
        idx.curr === 0 ? {} : props.updateIdx({ prev: idx.curr, curr: idx.curr - 1 });

    const transitionStyles: { [key: string]: any } =
        props.questionIdx.curr < props.questionIdx.prev
            ? {
                  entering: { transform: "translateY(-150%)" },
                  entered: { transform: "translateY(0%)" },
                  exiting: { transform: "translateY(150%)" },
                  exited: { transform: "translateY(-150%)" },
              }
            : {
                  entering: { transform: "translateY(150%)" },
                  entered: { transform: "translateY(0%)" },
                  exiting: { transform: "translateY(-150%)" },
                  exited: { transform: "translateY(150%)" },
              };

    return (
        <Container style={{ textAlign: "center" }}>
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "1fr",
                }}
            >
                <Header
                    station={props.station}
                    handleExit={props.handleExit}
                    questionIdx={props.questionIdx}
                    title={"Review"}
                    percentage={10 + (100 * idx.curr) / questionCount}
                    timer={false}
                    nextFunc={nextFunc}
                    prevFunc={prevFunc}
                />
                {props.station.questions.map((q, questionIdx) => (
                    <Transition
                        in={questionIdx === props.questionIdx.curr}
                        timeout={500}
                        mountOnEnter
                        unmountOnExit
                    >
                        {state => (
                            <QuestionReviewContent
                                question={q}
                                questionIdx={questionIdx}
                                transitionStyle={transitionStyles[state]}
                                updateStation={props.updateStation}
                                reviewMode={props.reviewMode}
                            />
                        )}
                    </Transition>
                ))}
            </div>
        </Container>
    );
};

export default QuestionReviewPage;
