import { useState } from "react";
import styled from "styled-components";
import { questions } from "./exampleQuestions";
import pneumo from "./Pneumothorax.png";

type QuestionSelectorProps = {};

const QuestionSelector = (props: QuestionSelectorProps) => {
    // these will be null in the actual implementatiuon
    // initalised once questions are retrieved
    const [selectedQuestion, updateSelectedQuestion] = useState(questions[0]);
    const [selectedFinding, updateSelectedFinding] = useState(questions[0].finding);
    const [selectedCategory, updateSelectedCategory] = useState(questions[0].category);

    // load these using use effect and initialise selection
    // for now using local dummy questions for UI testing
    // useEffect( sommin => quesitons! )

    const categories = Array(...new Set(questions.map(q => q.category)));

    return (
        <Container>
            <div style={{ width: "15%" }}>
                {categories.map(c => {
                    const findings = Array(
                        ...new Set(questions.filter(q => q.category === c).map(q => q.finding))
                    );

                    return (
                        <CategoryPanel selected={c === selectedCategory}>
                            <CategoryTitle>{c}</CategoryTitle>
                            {findings.map(f => (
                                <FindingOption
                                    selected={f === selectedFinding && c === selectedCategory}
                                    onClick={() => {
                                        updateSelectedFinding(f);
                                        updateSelectedCategory(c);
                                    }}
                                >
                                    {f}
                                </FindingOption>
                            ))}
                        </CategoryPanel>
                    );
                })}
            </div>
            <div style={{ display: "flex", backgroundColor: "#1c2124", width: "80%" }}>
                <div style={{ width: "25%" }}>
                    {questions
                        .filter(
                            q => q.category === selectedCategory && q.finding === selectedFinding
                        )
                        .map(q => (
                            <FindingOption
                                selected={
                                    q.category === selectedCategory && q.finding === selectedFinding
                                }
                            >
                                {q.title}
                            </FindingOption>
                        ))}
                </div>
                <div style={{ width: "75%" }}>
                    <QuestionSummary>
                        <div style={{ fontSize: "30px" }}>{selectedQuestion.title}</div>
                        {/* image would be fetched from source in actual implementation */}
                        <Img src={pneumo} />
                    </QuestionSummary>
                </div>
            </div>
        </Container>
    );
};

export default QuestionSelector;

const Container = styled.div`
    background-color: #2f3237;
    margin: 5%;
    padding: 20px 10px;
    display: flex;
    justify-content: space-around;
`;

const FindingOption = styled.div<{ selected: boolean }>`
    background-color: #2f3237;
    margin: 5%;
    padding: 5%;
    display: flex;
    cursor: pointer;
`;

const CategoryTitle = styled.div`
    font-size: 20px;
    margin: 5px;
`;

const CategoryPanel = styled.div<{ selected: boolean }>`
    text-align: center;
    margin-bottom: 10px;
    padding: 10px 5px;
    background-color: #1c2124;
`;

const QuestionSummary = styled.div`
    text-align: center;
    background-color: #2f3237;
    margin: 10px;
`;

const Img = styled.img`
    height: 300px;
`;
