import { Dispatch, MutableRefObject, SetStateAction, useEffect, useState } from "react";
import { FaMicrophone, FaPause, FaPlay } from "react-icons/fa";
import styled from "styled-components";
import WaveSurfer from "wavesurfer.js";
import { waveSurferParams } from "./hooks";
import { Question, Station_new, Task } from "./types";

type TaskSectionReviewProps = {
    containerId: string;
    playing: boolean;
    play: () => void;
    stop: () => void;
}

const TaskSectionReview = (props: TaskSectionReviewProps) => {

    const buttonStyle = { borderRadius: "50%", border: "1px solid #FAD000", padding: "5px", margin: "5px" }

    return (

        <PlaybackControl 
            busy = { props.playing } 
            onClick = { props.playing ? props.stop : props.play }
        >
            <div style = {{ width: "20%" }}>
                <ToggleButton busy = { props.playing }>
                    { props.playing ? <FaPause/> : <FaPlay/> } 
                </ToggleButton>
            </div>

            <div id = { props.containerId } style = {{ width: "50%" }}></div>

        </PlaybackControl>
    )
}

export default TaskSectionReview;

const PlaybackControl = styled.button<{ busy: boolean }>`
    display: flex;
    align-items: center;
    justify-content: center;
    border-style: none;
    background-color: transparent;
    border: 1px solid ${ props => props.busy ? "red" : "grey" };
    border-radius: 5px;
    color: ${ props => props.busy ? "red" : "#FAD000" };
    margin: auto;
    width: 100%;
`

const ToggleButton = styled.button<{ busy: boolean }>`
    height: 35px;
    width: 35px;
    border-radius: 50%;
    border: 1px solid ${ props => props.busy ? "red" : "#FAD000" };
    color: ${ props => props.busy ? "red" : "#FAD000" };
    margin: 10px;
    background-color: transparent;
`
