import { FaVolumeUp } from "react-icons/fa";
import styled from "styled-components";
import { promptId, Question } from "./types";
import heart from "./media/heart.png";
import { useContext } from "react";
import MetaContext from "../../MetaContext";
import { useWindowSize } from "../../functions";

type QuestionPromptProps = {
    question: Question;
    toggle: () => void;
};

const QuestionPrompt = (props: QuestionPromptProps) => {
    const { SERVER_URL } = useContext(MetaContext);
    const [w, _] = useWindowSize();
    const narrowWindow = w < 500;

    return (
        <div style={{ display: "flex", alignItems: "center" }}>
            <div style={{ margin: "20px 0" }}>
                <img src={heart} width={narrowWindow ? "35px" : "75px"} />
            </div>
            <div>
                <Prompt onClick={props.toggle}>
                    <FaVolumeUp />{" "}
                    <span style={{ fontSize: narrowWindow ? "12px" : "15px" }}>
                        &nbsp; &nbsp; {props.question.question}
                    </span>
                    <audio src={props.question.questionAudio} id={promptId} />
                </Prompt>
            </div>
        </div>
    );
};

export default QuestionPrompt;

const Prompt = styled.div`
    display: inline-block;
    color: #fad000;
    border: 2px solid #fad000;
    border-radius: 5px;
    margin: 0 20px;
    padding: 5px;
    &:hover {
        cursor: pointer;
    }
`;
