import { Dispatch, SetStateAction, useContext, useEffect, useState } from "react";
import { BrowserRouter, Link, match, Route, useLocation } from "react-router-dom";
import styled from "styled-components";
import WaveSurfer from "wavesurfer.js";
import MetaContext from "../../../MetaContext";
import { Box, MoveOn, Container } from "../Common";
import { generateWaveSurfer } from "../hooks";
import MarkScheme from "../Question/MarkScheme";
import QuestionPrompt from "../QuestionPrompt";
import TaskSectionReview from "./PlaybackControl";
import { Station, exemplarId, responseId, promptId, Question } from "../types";
import { CSSTransition, Transition, TransitionGroup } from "react-transition-group";
import QuestionReviewContent from "./QuestionReviewContent";

type QuestionReviewPageProps = {
    station: Station;
    updateStation: Dispatch<SetStateAction<Station>>;
    questionIdx: { prev: number; curr: number };
};

const QuestionReviewPage = (props: QuestionReviewPageProps) => {
    // cease playback when compnent unmounts <=== six hour bug :)
    // useEffect(
    //     () => stopAllPlayback,
    //     [audioState.exemplarWaveSurfer, audioState.responseWaveSurfer]
    // );

    const transitionStyles: { [key: string]: any } =
        props.questionIdx.curr < props.questionIdx.prev
            ? {
                  entering: { transform: "translateY(-150%)" },
                  entered: { transform: "translateY(0%)" },
                  exiting: { transform: "translateY(150%)" },
                  exited: { transform: "translateY(-150%)" },
              }
            : {
                  entering: { transform: "translateY(150%)" },
                  entered: { transform: "translateY(0%)" },
                  exiting: { transform: "translateY(-150%)" },
                  exited: { transform: "translateY(150%)" },
              };

    return (
        <Container style={{ textAlign: "center" }}>
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "1fr",
                }}
            >
                {props.station.questions.map((q, questionIdx) => (
                    <Transition
                        in={questionIdx === props.questionIdx.curr}
                        timeout={500}
                        mountOnEnter
                        unmountOnExit
                    >
                        {state => (
                            <QuestionReviewContent
                                question={q}
                                questionIdx={questionIdx}
                                transitionStyle={transitionStyles[state]}
                            />
                        )}
                    </Transition>
                ))}
            </div>
        </Container>
    );
};

export default QuestionReviewPage;
