import { Dispatch, MutableRefObject, SetStateAction, useEffect, useState } from "react";
import { FaPause, FaPlay } from "react-icons/fa";
import styled from "styled-components";
import { generateWaveSurfer } from "../hooks";

type TaskSectionProps = {
    responseWaveSurfer: WaveSurfer | undefined;
    playResponse: () => void;
    stopEverything: () => void;
    responseAudio: string;
    questionIdx: number;
    updateResponseWaveSurfer: Dispatch<SetStateAction<WaveSurfer | undefined>>;
};

const TaskSection = (props: TaskSectionProps) => {
    const [responsePlaying, updateResponsePlaying] = useState(false);

    useEffect(() => {
        if (props.responseWaveSurfer !== undefined)
            updateResponsePlaying(props.responseWaveSurfer.isPlaying());
    }, []);

    useEffect(() => {
        const container = document.getElementById("waveform" + props.questionIdx);
        if (container) container.innerHTML = "";

        props.updateResponseWaveSurfer(
            generateWaveSurfer("waveform" + props.questionIdx, props.responseAudio)
        );
    }, [props.responseAudio]);

    return (
        <div style={{ display: "flex", justifyContent: "space-between" }}>
            {!props.responseAudio ? (
                <>
                    <Panel
                        style={{ margin: "10px 0", width: "10%", backgroundColor: "#242528" }}
                    ></Panel>
                    <Panel
                        style={{
                            margin: "10px 0",
                            width: "87%",
                            backgroundColor: "#242528",
                        }}
                    >
                        <div id={"waveform" + props.questionIdx}></div>
                    </Panel>
                </>
            ) : (
                <>
                    <Panel
                        onClick={() => {
                            console.log(props.responseWaveSurfer?.isPlaying());
                            props.responseWaveSurfer?.isPlaying()
                                ? props.stopEverything()
                                : props.playResponse();
                        }}
                        style={{ margin: "10px 0", width: "10%" }}
                    >
                        {responsePlaying ? <FaPause /> : <FaPlay />}
                    </Panel>
                    <Panel style={{ margin: "10px 0", width: "87%" }}>
                        <div id={"waveform" + props.questionIdx}></div>
                    </Panel>
                </>
            )}
        </div>
    );
};

export default TaskSection;

const Panel = styled.div`
    background-color: #303237;
    padding: 0 10px;
    border-radius: 10px;
`;
