import { Dispatch, ReactElement, SetStateAction, useEffect } from "react";
import { Link, Redirect } from "react-router-dom";
import { Box, Timer, Container, StartButton } from "./Common";
import { useLoopTime } from "./hooks";
import TimerBar from "./TimerBar";
import { Station } from "./types";
import htmlParser from "react-html-parser";

type BriefProps = {
    station: Station;
    updateStation: Dispatch<SetStateAction<Station>>;
};

const Brief = (props: BriefProps) => {
    const secondTicker = useLoopTime(props.station.brief.timeRemaining);

    if (secondTicker <= 0) {
        return <Redirect to="/OSCE/Station/0" />;
    }

    return (
        <Container>
            <div
                style={{
                    position: "sticky",
                    top: 0,
                    width: "100%",
                    backgroundColor: "#1C2124",
                    padding: "50px 0 20px 0",
                    opacity: "0.98",
                }}
            >
                <h2>Brief</h2>

                <br></br>

                <TimerBar percentage={5} />
            </div>

            <br></br>

            <Box style={{}}>
                <div style={{ textAlign: "center" }}>
                    <em>Time left to read:</em>
                    <Timer>
                        {" "}
                        00 : {secondTicker < 10 ? "0" : ""}
                        {secondTicker}{" "}
                    </Timer>
                </div>

                <br />

                {htmlParser(props.station.brief.prompt)}

                <br></br>

                <Link to="/OSCE/Station/0">
                    <StartButton style={{ margin: "auto" }}>Start Station</StartButton>
                </Link>
            </Box>
        </Container>
    );
};

export default Brief;
