import { Dispatch, SetStateAction } from "react";
import { useHistory } from "react-router-dom";
import { HeaderDetails } from "./types";
import { History, LocationState } from "history";

export const headerDetailSwitch = (
    lastPath: string,
    questionCount: number,
    idx: { prev: number; curr: number },
    updateidx: Dispatch<SetStateAction<{ prev: number; curr: number }>>,
    history: History<LocationState>
): HeaderDetails => {
    if (lastPath === "Brief")
        return {
            title: "Brief",
            percentage: 5,
            briefAndExit: false,
            timer: false,
            nextFunc: () => history.push("/OSCE/Station"),
            prevFunc: () => history.push("/OSCE/Station"),
        };
    else if (lastPath === "Question") {
        return {
            title: "Station One",
            percentage: 10 + (100 * idx.curr) / questionCount,
            briefAndExit: true,
            timer: true,
            nextFunc: () =>
                idx.curr === questionCount - 1
                    ? history.push("/OSCE/Station/Summary")
                    : updateidx({ prev: idx.curr, curr: idx.curr + 1 }),
            prevFunc: () =>
                idx.curr === 0 ? {} : updateidx({ prev: idx.curr, curr: idx.curr - 1 }),
        };
    } else if (lastPath === "Summary")
        return {
            title: "Finish",
            percentage: 100,
            briefAndExit: true,
            timer: false,
            nextFunc: () => {
                updateidx({ prev: -1, curr: 0 });
                history.push("/OSCE/Station/Review");
            },
            prevFunc: () => {},
        };
    else if (lastPath === "Review") {
        return {
            title: "Review",
            percentage: 10 + (100 * idx.curr) / questionCount,
            briefAndExit: true,
            timer: false,
            nextFunc: () =>
                idx.curr === questionCount - 1
                    ? history.push("/OSCE/Station/MarkReview")
                    : updateidx({ prev: idx.curr, curr: idx.curr + 1 }),
            prevFunc: () =>
                idx.curr === 0 ? {} : updateidx({ prev: idx.curr, curr: idx.curr - 1 }),
        };
    } else
        return {
            title: "Summary",
            percentage: 100,
            briefAndExit: true,
            timer: false,
            nextFunc: () => {},
            prevFunc: () => {
                updateidx({ prev: questionCount, curr: questionCount - 1 });
                history.push("/OSCE/Station/Review");
            },
        };
};
