import { Dispatch, SetStateAction, useContext, useEffect, useState } from "react";
import { FaPause, FaPlay } from "react-icons/fa";
import styled from "styled-components";
import MetaContext from "../../../MetaContext";
import { Box } from "../Common";
import { generateWaveSurfer } from "../hooks";
import { AudioState } from "./QuestionReviewContent";

type WaveSurferReviewProps = {
    playing: boolean;
    play: () => void;
    stopEverything: () => void;
    responseAudio: string;
    exemplarAudio: string;
    updateAudioState: Dispatch<SetStateAction<AudioState>>;
    questionIdx: number;
};

const WaveSurferReview = (props: WaveSurferReviewProps) => {
    const [responseSelected, updateResponseSelected] = useState(true);
    const { narrowWindow } = useContext(MetaContext);

    const loadSurfer = (audio: string) => {
        props.stopEverything();
        const container = document.getElementById("waveform" + props.questionIdx);
        if (container) container.innerHTML = "";

        const waveSurfer = generateWaveSurfer("waveform" + props.questionIdx, audio);
        props.updateAudioState(as => ({
            ...as,
            waveSurfer,
        }));
        waveSurfer.on("ready", () => waveSurfer.play());
    };

    return (
        <div style={{ display: "flex", justifyContent: "space-between" }}>
            <AudioSelect
                onClick={() => {
                    if (responseSelected) props.playing ? props.stopEverything() : props.play();
                    else {
                        updateResponseSelected(true);
                        loadSurfer(props.responseAudio);
                    }
                }}
                narrowWindow={narrowWindow}
                selected={responseSelected}
            >
                You
            </AudioSelect>

            <AudioSelect
                onClick={() => {
                    if (!responseSelected) props.playing ? props.stopEverything() : props.play();
                    else {
                        updateResponseSelected(false);
                        loadSurfer(props.exemplarAudio);
                    }
                }}
                narrowWindow={narrowWindow}
                selected={!responseSelected}
            >
                Exemplar
            </AudioSelect>

            <Box style={{ margin: "10px 0", width: "87%" }}>
                <div id={"waveform" + props.questionIdx}></div>
            </Box>
        </div>
    );
};

export default WaveSurferReview;

const AudioSelect = styled.button<{ selected: boolean; narrowWindow: boolean }>`
    background-color: ${props => (props.selected ? "green" : "#414142")};
    border-radius: 5px;
    height: 35px;
    align-content: center;
    width: 30%;
    border: 1px solid #414142;
    color: ${props => (props.selected ? "white" : "#FAD000")};
    font-size: ${props => (props.narrowWindow ? "12px" : "15px")};
`;
