import { FaVolumeUp } from "react-icons/fa";
import styled from "styled-components";
import { promptId, Question } from "./types";
import heart from "./media/heart.png";
import { useContext } from "react";
import MetaContext from "../../MetaContext";

type QuestionPromptProps = {
    question: Question;
    toggle: () => void;
};

const QuestionPrompt = (props: QuestionPromptProps) => {
    const { SERVER_URL } = useContext(MetaContext);

    return (
        <div style={{}}>
            <div style={{ display: "inline-block", margin: "20px 0" }}>
                <img src={heart} width="100px" />
            </div>

            <Prompt onClick={props.toggle}>
                <FaVolumeUp /> &nbsp; &nbsp; {props.question.question}
                <audio autoPlay={true} src={props.question.questionAudio} id={promptId} />
            </Prompt>
        </div>
    );
};

export default QuestionPrompt;

const Prompt = styled.div`
    display: inline-block;
    color: #fad000;
    border: 2px solid #fad000;
    border-radius: 5px;
    margin: 0 20px;
    padding: 5px;
    &:hover {
        cursor: pointer;
    }
`;
