import { Dispatch, SetStateAction, useContext, useEffect, useState } from "react";
import { Link, Redirect } from "react-router-dom";
import styled from "styled-components";
import MetaContext from "../../MetaContext";
import { getBinaryDigits, markPercentage } from "./hooks";
import { AnsweredStationDetails } from "./types";

type StationSummaryCardProps = {
    summary: {
        id: string;
        thumbnail: string;
        title: string;
    };
    selectStation: (id: string, options: { review: boolean; marks: (0 | 1)[] }) => void;
    answeredStations: AnsweredStationDetails[];
    available: boolean;
};

const StationSummaryCard = (props: StationSummaryCardProps) => {
    const { SERVER_URL } = useContext(MetaContext);

    const matches = props.answeredStations.filter(s => s.id === props.summary.id);
    const percentage = matches.length === 0 ? 0 : markPercentage(matches[0].markCode);
    const marks = matches.length === 0 ? [] : getBinaryDigits(matches[0].markCode).slice(1);

    const handleClick = (review: boolean) => {
        props.selectStation(props.summary.id, { review, marks });
    };

    return (
        <>
            <StationPanel>
                <div style={{ margin: "20px" }}>
                    <img src={SERVER_URL + "/media/osce/" + props.summary.thumbnail} width="60pc" />
                </div>
                <div style={{ padding: "5px", textAlign: "left" }}>
                    <h5>{props.summary.title}</h5>

                    {props.available ? (
                        <>
                            <div>Last Attempt: {isNaN(percentage) ? "--" : percentage}%</div>
                            <Link to="/OSCE/Station/Brief">
                                <StartOrReview onClick={() => handleClick(false)}>
                                    Start
                                </StartOrReview>
                            </Link>
                            {matches.length > 0 && (
                                <Link to="/OSCE/Station/Summary">
                                    <StartOrReview onClick={() => handleClick(true)}>
                                        Review
                                    </StartOrReview>
                                </Link>
                            )}
                        </>
                    ) : (
                        <StartOrReview>Requires full access!</StartOrReview>
                    )}
                </div>
            </StationPanel>
            <br />
        </>
    );
};

export default StationSummaryCard;

const StationPanel = styled.div`
    display: flex;
    align-items: center;
    background-color: #1c2124;
    color: white;
    border-style: none;
    border-radius: 5px;
    width: 100%;
    max-width: 500px;
`;

const StartOrReview = styled.button`
    text-align: center;
    margin: 5px 10px 5px 0;
    border-radius: 25px;
    padding: 0 10px;
    height: 30px;
    border: 2px solid grey;
    color: #fad000;
    background-color: transparent;
`;
