import styled from "styled-components";
import Navigator from "../Navigator";
import Testimonial from "./Testimonial";
import "../HomePage/whatGPT3.css";
import "../HomePage/header.css";
import example from "../../images/Example.png";
import { Link } from "react-router-dom";
import Brain from "../../images/brainComputer.png";
import "@fontsource/plus-jakarta-sans";
import { FaVolumeUp } from "react-icons/fa";
import "aos/dist/aos.css";
import { useContext } from "react";
import MetaContext from "../../MetaContext";
import { Login, Logout } from "../HomePage/LoginLogout";

const HomePage = () => {
    const { token, setToken } = useContext(MetaContext);

    return (
        <>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                <LogoTitle>
                    <Link to="/">
                        <h1 style={{ fontFamily: "Jakarta" }}>MedBakery</h1>
                    </Link>
                </LogoTitle>
                {token ? (
                    <Logout darkMode={true} setToken={setToken} />
                ) : (
                    <Login label="Sign Up / Login" />
                )}
            </div>

            <Container>
                <div style={{ padding: "2%", maxWidth: "1400px", margin: "auto" }}>
                    <div className="gpt3__header section__padding" id="home">
                        <div className="gpt3__header-content">
                            <h1
                                style={{ fontFamily: "Jakarta", fontSize: 50, lineHeight: "-12%" }}
                                className="gradient__text">
                                Stop going blank in an OSCE
                            </h1>

                            <div
                                style={{
                                    display: "flex",
                                    flexFlow: "column",
                                    justifyContent: "flex-start",
                                    marginLeft: "-25px",
                                }}>
                                <div>
                                    <Prompt1
                                        style={{
                                            fontSize: "120%",
                                            width: "100%",
                                            padding: "5px 23px",
                                            textAlign: "left",
                                            justifyContent: "flex-start",
                                        }}>
                                        <FaVolumeUp
                                            style={{
                                                marginRight: "8px",
                                                marginTop: "3px",
                                                marginBottom: "3.5px",
                                            }}
                                        />
                                        The time pressure, being on the spot, tricky questions, data
                                        interp...it can be overwhelming, but we'll help you get
                                        better at it!
                                    </Prompt1>
                                    <br />

                                    <Prompt
                                        style={{
                                            fontSize: "120%",
                                            width: "100%",
                                            padding: "5px 23px",
                                            textAlign: "left",
                                            justifyContent: "flex-start",
                                            color: "#FFFFF",
                                        }}>
                                        <FaVolumeUp
                                            style={{
                                                marginRight: "8px",
                                                marginTop: "3px",
                                                marginBottom: "3.5px",
                                            }}
                                        />
                                        All our stations simulate the real exam environment and are
                                        based on past exams, come with detailed markschemes and
                                        model answers.
                                    </Prompt>
                                </div>
                            </div>
                        </div>

                        <div className="gpt3__header-image">
                            <img style={{ scale: "125%" }} src={Brain} />
                        </div>
                    </div>
                    <div
                        style={{ marginTop: "20px", marginLeft: "", marginBottom: "-20px" }}
                        className="gpt3__whatgpt3 section__margin"
                        id="wgpt3">
                        <div className="gpt3__header-image">
                            <img style={{ scale: "100%" }} src={example} />
                        </div>
                    </div>

                    <Testimonial />

                    <div
                        style={{ marginTop: "-65px", marginLeft: "", marginBottom: "-5px" }}
                        className="gpt3__whatgpt3 section__margin"
                        id="wgpt3">
                        <div
                            style={{
                                width: "100%",
                                textAlign: "center",
                                justifyContent: "center",
                            }}>
                            <Link to="/OSCE">
                                <DemoButton> Start Studying </DemoButton>
                            </Link>
                        </div>
                    </div>
                </div>
            </Container>
        </>
    );
};

export default HomePage;

const LogoTitle = styled.div`
    margin: 30px 5%;
`;

const Option = styled.div<{ av: boolean; selected: boolean }>`
    padding: 15px;
    background-color: ${props => (props.selected ? "#2f3237" : "none")};
    text-decoration: ${props => (props.av ? "none" : "line-through")};
    color: ${props => (props.selected ? "#fad000" : "white")};
    border-radius: 5px;
`;

const Container = styled.div`
    background-color: #2f3237;
    margin: 5%;
    padding: 5%;
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    border-radius: 10px;
`;

const DemoButton = styled.button`
    margin: 5% 5%;
    padding: 0% 10%;
    font-size: 35px;
    border: 1px solid #da9926;
    border-radius: 8px;
    background-color: #da9926;
    color: #000000;
    &:hover {
        drop-shadow: 2px;
        border: 1px solid black;
    }

    @media (max-width: 600px) {
        height: 40px;
        width: 150px;
        font-size: 15px;
    }
`;

const Prompt1 = styled.div`
    color: #da9926;
    border: 0.5px solid grey;
    border-radius: 8px;
    margin: 0.5% 5%;
    padding: 1% 0;
    &:hover {
        cursor: pointer;
        border: 1px solid #fad000;
    }
`;

const Prompt = styled.div`
    color: #dbdfea;
    border: 0.5px solid grey;
    border-radius: 8px;
    margin: 0.5% 5%;
    padding: 1% 0;
    &:hover {
        cursor: pointer;
        border: 1px solid #fad000;
    }
`;
