import { Dispatch, SetStateAction, useContext, useEffect, useState } from "react";
import { BrowserRouter, Link, match, Route, useLocation } from "react-router-dom";
import styled from "styled-components";
import WaveSurfer from "wavesurfer.js";
import MetaContext from "../../MetaContext";
import { Box, MoveOn, Container } from "./Common";
import { generateWaveSurfer } from "./hooks";
import MarkScheme from "./MarkScheme";
import QuestionPrompt from "./QuestionPrompt";
import TaskSectionReview from "./TaskSectionReview";
import { Station, exemplarId, responseId, promptId, Question } from "./types";
import { CSSTransition, Transition, TransitionGroup } from "react-transition-group";
import "./styles.css";

type QuestionReviewPageProps = {
    station: Station;
    updateStation: Dispatch<SetStateAction<Station>>;
    questionIdx: number;
};

type AudioState = {
    promptAudio: HTMLAudioElement | null;
    waveSurfer: WaveSurfer | undefined;
    exemplarPlaying: boolean;
    responsePlaiyng: boolean;
    promptPlaying: boolean;
};

const initalAudioState: AudioState = {
    promptAudio: null,
    promptPlaying: false,
    waveSurfer: undefined,
    exemplarPlaying: false,
    responsePlaiyng: false,
};

const QuestionReviewPage = (props: QuestionReviewPageProps) => {
    const { narrowWindow } = useContext(MetaContext);
    const [imageExpanded, updateImageExpanded] = useState(false);
    const [audioState, updateAudioState] = useState<AudioState>(initalAudioState);
    const [showMarkScheme, updateShowMarkScheme] = useState(true);

    const question = props.station.questions[props.questionIdx];

    const stopAllPlayback = () => {
        audioState.promptAudio?.pause();
        audioState.waveSurfer?.stop();
        updateAudioState(as => ({
            ...as,
            exemplarPlaying: false,
            responsePlaiyng: false,
            promptPlaying: false,
        }));
    };

    const play = (id: string) => {
        const container = document.getElementById("waveform");
        if (container) container.innerHTML = "";

        const newWaveSurfer = generateWaveSurfer(
            "waveform",
            id === exemplarId ? question.exemplarAudio : question.responseAudio,
            stopAllPlayback
        );

        stopAllPlayback();
        updateAudioState(as => ({
            ...as,
            promptPlaying: id == promptId,
            exemplarPlaying: id === exemplarId,
            responsePlaiyng: id === responseId,
            waveSurfer: newWaveSurfer,
        }));

        if (id === promptId) audioState.promptAudio?.play();
        else {
            newWaveSurfer?.on("ready", () => newWaveSurfer.play());
        }
    };

    useEffect(() => {
        stopAllPlayback();

        const container = document.getElementById("waveform");
        if (container) container.innerHTML = "";

        const promptAudio = document.getElementById(promptId) as HTMLAudioElement;
        promptAudio.play();

        updateAudioState({
            ...initalAudioState,
            promptAudio,
            promptPlaying: true,
            waveSurfer: generateWaveSurfer("waveform", question.responseAudio, stopAllPlayback),
        });
    }, [props.questionIdx]);

    // cease playback when compnent unmounts <=== six hour bug :)
    // useEffect(
    //     () => stopAllPlayback,
    //     [audioState.exemplarWaveSurfer, audioState.responseWaveSurfer]
    // );

    const defaultStyle = {
        gridRowStart: 1,
        gridColumnStart: 1,
        transition: `opacity ${1000}ms ease-in-out`,
        opacity: 0,
    };

    const transitionStyles: { [key: string]: any } = {
        entering: { opacity: 1 },
        entered: { opacity: 1 },
        exiting: { opacity: 0 },
        exited: { opacity: 0 },
    };

    return (
        <Container style={{ textAlign: "center" }}>
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "1fr",
                }}
            >
                {props.station.questions.map((_, questionIdx) => (
                    <Transition in={questionIdx === props.questionIdx} timeout={1000}>
                        {state => (
                            <div
                                style={{
                                    ...defaultStyle,
                                    ...transitionStyles[state],
                                    zIndex: questionIdx,
                                }}
                            >
                                <Box>
                                    <QuestionPrompt
                                        question={question}
                                        toggle={() => {
                                            audioState.promptPlaying
                                                ? stopAllPlayback()
                                                : play(promptId);
                                        }}
                                    />

                                    {showMarkScheme ? (
                                        <div
                                            style={{
                                                textAlign: "center",
                                                position: "relative",
                                                marginTop: "30px",
                                            }}
                                        >
                                            <Img
                                                src={question.img}
                                                imageExpanded={imageExpanded}
                                                onClick={() => updateImageExpanded(!imageExpanded)}
                                            ></Img>

                                            <div
                                                style={{
                                                    position: "absolute",
                                                    top: -25,
                                                    width: "100%",
                                                    color: "grey",
                                                }}
                                            >
                                                Click to {imageExpanded ? "shrink" : "expand"}!
                                            </div>
                                        </div>
                                    ) : (
                                        <MarkScheme
                                            updateStation={props.updateStation}
                                            markScheme={question.markScheme}
                                            questionIdx={questionIdx}
                                        />
                                    )}
                                    <br />
                                </Box>
                            </div>
                        )}
                    </Transition>
                ))}

                <div style={{ gridRowStart: 2 }}>
                    <br />
                    <Box>
                        <div id="waveform"></div>
                    </Box>
                    <br />

                    <div style={{ display: "flex", justifyContent: "space-between" }}>
                        <div style={{ width: "30%" }}>
                            <TaskSectionReview
                                label="You"
                                playing={audioState.responsePlaiyng}
                                play={() => play(responseId)}
                                stop={stopAllPlayback}
                            />
                        </div>

                        <div style={{ width: "30%" }}>
                            <SchemeToggle
                                narrowWindow={narrowWindow}
                                onClick={() => updateShowMarkScheme(!showMarkScheme)}
                            >
                                Mark Scheme
                            </SchemeToggle>
                        </div>

                        <div style={{ width: "30%" }}>
                            <TaskSectionReview
                                label="Exemplar"
                                playing={audioState.exemplarPlaying}
                                play={() => play(exemplarId)}
                                stop={stopAllPlayback}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </Container>
    );
};

export default QuestionReviewPage;

const Img = styled.img<{ imageExpanded: boolean }>`
    display: block;
    margin: auto;
    height: 250px;
    transition: width 0.2s;
    &:hover {
        cursor: pointer;
    }
`;

const SchemeToggle = styled.button<{ narrowWindow: boolean }>`
    background-color: #414142;
    border-radius: 5px;
    height: 35px;
    align-content: center;
    margin: auto;
    width: 100%;
    border: 1px solid #414142;
    color: #fad000;
    font-size: ${props => (props.narrowWindow ? "12px" : "15px")};
`;
