import { FaMicrophone, FaPause, FaPlay } from "react-icons/fa";
import styled from "styled-components";
import { responseId } from "./types";
import { useReactMediaRecorder } from "react-media-recorder";
import { playStartTone } from "./hooks";

type TaskSectionProps = {
    recording: boolean;
    recordResponse: () => void;
    playing: boolean;
    playResponse: () => void;
    stopEverything: () => void;
    responseAvailable: boolean;
    loadingRecorder: boolean;
};

const TaskSection = (props: TaskSectionProps) => {
    return (
        <div>
            <PlaybackControl
                busy={props.playing}
                onClick={props.playing ? props.stopEverything : props.playResponse}
                display={props.responseAvailable}
            >
                <div style={{ width: "20%" }}>
                    <ToggleButton busy={props.playing}>
                        {props.playing ? <FaPause /> : <FaPlay />}
                    </ToggleButton>
                </div>

                <div id={responseId} style={{ width: "50%" }}></div>
            </PlaybackControl>

            <br />

            <MicControl
                busy={props.recording}
                onClick={
                    props.recording
                        ? props.stopEverything
                        : () => {
                              props.recordResponse();
                              playStartTone();
                          }
                }
                display={!props.responseAvailable}
            >
                <ToggleButton busy={props.recording}>
                    <FaMicrophone />
                </ToggleButton>
                {props.loadingRecorder && "Please Wait...."}
                {props.recording && "Answer now!"}
                {!(props.loadingRecorder || props.recording) && "Click to answer!"}
            </MicControl>
        </div>
    );
};

export default TaskSection;

const MicControl = styled.button<{ busy: boolean; display: boolean }>`
    display: ${props => (props.display ? "block" : "none")};
    border-style: none;
    background-color: transparent;
    border: 1px solid ${props => (props.busy ? "red" : "grey")};
    border-radius: 5px;
    color: ${props => (props.busy ? "red" : "#FAD000")};
    margin: auto;
    width: 100%;
`;

const PlaybackControl = styled.button<{ busy: boolean; display: boolean }>`
    display: ${props => (props.display ? "flex" : "none")};
    align-items: center;
    justify-content: center;
    border-style: none;
    background-color: transparent;
    border: 1px solid ${props => (props.busy ? "red" : "grey")};
    border-radius: 5px;
    color: ${props => (props.busy ? "red" : "#FAD000")};
    margin: auto;
    width: 100%;
`;

const ToggleButton = styled.button<{ busy: boolean }>`
    height: 35px;
    width: 35px;
    border-radius: 50%;
    border: 1px solid ${props => (props.busy ? "red" : "#FAD000")};
    color: ${props => (props.busy ? "red" : "#FAD000")};
    margin: 10px;
    background-color: transparent;
`;
