import { useContext, useEffect, useState } from "react";
import styled from "styled-components";
import MetaContext from "../../MetaContext";
import { Login, LoginButton } from "../HomePage/LoginLogout";
import { StartButton } from "./Common";
import TimerBar from "./TimerBar";
import { Question, Station } from "./types";
import htmlParser from "react-html-parser";
import { Link, useLocation } from "react-router-dom";
import AlphaTimer from "./AlphaTimer";

type HeaderProps = {
    station: Station;
    handleExit: (questions: Question[], tok: string) => void;
};

const DefaultDetails = {
    title: "Brief",
    percentage: 5,
    briefAndExit: false,
    timer: false,
    pathToNext: "/OSCE",
};

const Header = (props: HeaderProps) => {
    const { token, SERVER_URL } = useContext(MetaContext);
    const [briefShowing, updateBriefShowing] = useState(false);
    const [displayLoginOption, updateDisplayLoginOption] = useState(false);
    const [headerDetails, updateHeaderDetails] = useState(DefaultDetails);

    const location = useLocation();
    const splitPaths = location.pathname.split("/");
    const lastPath = splitPaths[splitPaths.length - 1];

    useEffect(() => {
        if (lastPath === "Station") {
            updateHeaderDetails({
                title: "Brief",
                percentage: 5,
                briefAndExit: false,
                timer: false,
                pathToNext: "/OSCE/Station/0",
            });
        } else if (Number.isInteger(Number(lastPath))) {
            const i = Number(lastPath);
            const percentage = 10 + (100 * i) / props.station.questions.length;
            updateHeaderDetails({
                title: "Station One",
                percentage,
                briefAndExit: true,
                timer: true,
                pathToNext:
                    "/OSCE/Station/" +
                    (i === props.station.questions.length - 1 ? "Summary" : i + 1),
            });
        } else if (lastPath === "Summary") {
            updateHeaderDetails({
                title: "Station One",
                percentage: 100,
                briefAndExit: true,
                timer: false,
                pathToNext: "/OSCE/Station/0",
            });
        } else if (lastPath.startsWith("Review")) {
            const questionIdx = Number(lastPath.substring(lastPath.indexOf("w") + 1));
            const percentage = 10 + (100 * questionIdx) / props.station.questions.length;
            updateHeaderDetails({
                title: "Station One Review",
                percentage,
                briefAndExit: true,
                timer: false,
                pathToNext: "/OSCE/Station/0",
            });
        } else {
            updateHeaderDetails({
                title: "Station One Review",
                percentage: 100,
                briefAndExit: true,
                timer: false,
                pathToNext: "/OSCE/Station/0",
            });
        }
    }, [location.pathname]);

    return (
        <HeaderContainer>
            <div style={{ display: "flex", justifyContent: "space-between", height: "40px" }}>
                <div>
                    <h2>{headerDetails.title}</h2>
                </div>
                {headerDetails.timer && (
                    <AlphaTimer
                        startTime={props.station.timePermitted}
                        pathToNext="/OSCE/Station/"
                    />
                )}
            </div>

            <TimerBar percentage={headerDetails.percentage} />

            {headerDetails.briefAndExit && (
                <div style={{ display: "flex", justifyContent: "space-between" }}>
                    <div>
                        <StartButton onClick={() => updateBriefShowing(!briefShowing)}>
                            {briefShowing ? "Hide" : "Show"} Brief
                        </StartButton>
                    </div>

                    {token ? (
                        <StartButton
                            onClick={() => props.handleExit(props.station?.questions, token)}
                        >
                            Exit Demo
                        </StartButton>
                    ) : (
                        <StartButton onClick={() => updateDisplayLoginOption(true)}>
                            Exit Demo
                        </StartButton>
                    )}
                </div>
            )}

            <br />

            {displayLoginOption ? (
                <div style={{ textAlign: "center" }}>
                    <Login
                        label={"Login & Save!"}
                        successCallback={t => props.handleExit(props.station?.questions, t)}
                    />
                    <Link to="/OSCE">
                        <LoginButton>Exit without saving.</LoginButton>
                    </Link>
                </div>
            ) : (
                <></>
            )}

            {briefShowing ? htmlParser(props.station.brief.prompt) : <></>}
        </HeaderContainer>
    );
};

export default Header;

const HeaderContainer = styled.div`
    position: sticky;
    top: 0;
    max-width: 800px;
    margin: auto;
    background-color: #1c2124;
    padding: 20px 20px 0 20px;
    opacity: 0.98;
    z-index: 999;
`;
