import { useContext, useState, useEffect, Dispatch, SetStateAction } from "react";
import styled from "styled-components";
import MetaContext from "../../../MetaContext";
import { Box } from "../Common";
import QuestionPrompt from "../QuestionPrompt";
import { Question, promptId, responseId, Station } from "../types";
import MarkScheme from "./MarkScheme";
import WaveSurferReview from "./WaveSurferReview";

type QuestionReviewContentProps = {
    question: Question;
    questionIdx: number;
    transitionStyle: any;
    updateStation: Dispatch<SetStateAction<Station>>;
    reviewMode: boolean;
};

export type AudioState = {
    waveSurfer: WaveSurfer | undefined;
    responsePlaiyng: boolean;
    promptPlaying: boolean;
};

const getPromptAudio = (idx: number) => document.getElementById(promptId + idx) as HTMLAudioElement;

const QuestionReviewContent = (props: QuestionReviewContentProps) => {
    const { narrowWindow } = useContext(MetaContext);
    const [audioState, updateAudioState] = useState<AudioState>({
        promptPlaying: true,
        waveSurfer: undefined,
        responsePlaiyng: false,
    });
    const [showMarkScheme, updateShowMarkScheme] = useState(true);
    const [selection, updateSelection] = useState("exemplar");

    const stopEverything = () => {
        getPromptAudio(props.questionIdx).pause();
        audioState.waveSurfer?.stop();
        updateAudioState(as => ({
            ...as,
            responsePlaiyng: false,
            promptPlaying: false,
        }));
    };

    const play = (id: string) => {
        stopEverything();
        updateAudioState(as => ({
            ...as,
            promptPlaying: id == promptId,
            responsePlaiyng: id === responseId,
        }));

        if (id === promptId) getPromptAudio(props.questionIdx).play();
        else audioState.waveSurfer?.play();
    };

    // stop wavesurfer when transitions active or playback selection changes
    useEffect(() => {
        audioState.waveSurfer?.stop();
        updateAudioState(as => ({
            ...as,
            responsePlaiyng: false,
        }));
    }, [props.transitionStyle, selection]);

    return (
        <>
            <QuestionPrompt
                question={props.question}
                toggle={() => {
                    audioState.promptPlaying ? stopEverything() : play(promptId);
                }}
                promptId={promptId + props.questionIdx}
            />
            <div
                style={{
                    gridRowStart: 3,
                    gridColumnStart: 1,
                    height: "55vh",
                    transition: `transform ${500}ms`,
                    ...props.transitionStyle,
                }}
            >
                <Box style={{ padding: "10px" }}>
                    {showMarkScheme ? (
                        <Img src={props.question.img} narrowWindow={narrowWindow}></Img>
                    ) : (
                        <MarkScheme
                            markScheme={props.question.markScheme}
                            questionIdx={props.questionIdx}
                            updateStation={props.updateStation}
                            reviewMode={props.reviewMode}
                        />
                    )}
                </Box>
            </div>
            <div
                style={{
                    gridRowStart: 4,
                    gridColumnStart: 1,
                    zIndex: 1000,
                    backgroundColor: "#1c2124",
                    height: "15vh",
                }}
            >
                <WaveSurferReview
                    playing={audioState.responsePlaiyng}
                    stopEverything={stopEverything}
                    play={() => play(responseId)}
                    exemplarAudio={props.question.exemplarAudio}
                    responseAudio={props.question.responseAudio}
                    updateAudioState={updateAudioState}
                    questionIdx={props.questionIdx}
                />

                <SchemeToggle
                    narrowWindow={narrowWindow}
                    onClick={() => updateShowMarkScheme(!showMarkScheme)}
                >
                    {showMarkScheme ? "Mark Scheme" : "Show Station"}
                </SchemeToggle>
            </div>
        </>
    );
};

export default QuestionReviewContent;

const Img = styled.img<{ narrowWindow: boolean }>`
    display: block;
    margin: auto;
    height: ${props => (props.narrowWindow ? "250px" : "450px")};
    max-width: 100%;
    transition: width 0.2s;
`;

const SchemeToggle = styled.button<{ narrowWindow: boolean }>`
    background-color: #414142;
    border-radius: 5px;
    height: 35px;
    align-content: center;
    width: 30%;
    border: 1px solid #414142;
    color: #fad000;
    font-size: ${props => (props.narrowWindow ? "12px" : "15px")};
`;

const AudioSelect = styled.button<{ selected: boolean; narrowWindow: boolean }>`
    background-color: ${props => (props.selected ? "green" : "#414142")};
    border-radius: 5px;
    height: 35px;
    align-content: center;
    width: 30%;
    border: 1px solid #414142;
    color: ${props => (props.selected ? "white" : "#FAD000")};
    font-size: ${props => (props.narrowWindow ? "12px" : "15px")};
`;
