import axios from "../../myaxios";
import { Link } from "react-router-dom";
import styled from "styled-components";
import Navigator from "../Navigator";
import { NullStation } from "../OSCEPage/DemoStations/NullStation";
import ProgressPie from "./ProgressPie";
import { useContext, useEffect, useState } from "react";
import MetaContext from "../../MetaContext";
import { markPercentage, useUserDetails } from "../OSCEPage/hooks";
import { AnsweredStationDetails, StationSummary } from "../OSCEPage/types";

type UserPageProps = {};

const UserPage = (props: UserPageProps) => {
    const { SERVER_URL, token } = useContext(MetaContext);
    const [{ answeredStations, purchasedOsce }, userLoading] = useUserDetails(SERVER_URL, token);

    const averagePercentage =
        answeredStations.length === 0
            ? 0
            : answeredStations.map(as => markPercentage(as.markCode)).reduce((acc, a) => acc + a) /
              answeredStations.length;

    const lastAttemptedStation = answeredStations.reduce((las, as) =>
        as.dateAnswered > las.dateAnswered ? as : las
    );

    const getAnsweredStation = async () => {
        try {
            const axres = await axios.post(SERVER_URL + "/osce/get_station", { token, id });

            if (!axres.data.success) {
                throw "selecteStation (OSCEPage): Station retrieval failed!";
            }

            if (options.review) {
                const matches = userDetailsOsce.answeredStations.filter(
                    s => s.id === axres.data.station.id
                );
                const rawResponseAudios =
                    matches.length === 0
                        ? Array(axres.data.station.questions.length).fill("")
                        : matches[0].responseAudios;

                const stationWithMarks = populateMarkScheme(axres.data.station, options.marks);
                const stationWithResponses = populateResponseAudios(
                    stationWithMarks,
                    rawResponseAudios
                );
                const localContentStation = await downloadAssets(stationWithResponses, SERVER_URL);
                updateStation(localContentStation);
            }
        } catch (e) {}
    };

    return (
        <>
            <Navigator selection={"Home"} />

            <Container>
                <div style={{ display: "flex", justifyContent: "space-around" }}>
                    <div style={{ width: "40%", textAlign: "center" }}>
                        <div style={{ fontSize: "50px" }}>Welcome Back</div>
                        <br />
                        <Panel>
                            <Title>Average OSCE Score</Title>
                            <ProgressPie percentage={averagePercentage} radius={75} />
                            <Button>Analysis</Button>
                        </Panel>

                        <Panel>
                            <Title>Review Last Station</Title>
                            <Link
                                to={{ pathname: "/OSCE/Station", state: { lastAttemptedStation } }}
                            >
                                <Button>Analysis</Button>
                            </Link>
                        </Panel>
                    </div>
                    <div style={{ width: "40%", textAlign: "center" }}>
                        <Panel>
                            <Title>Breakdown</Title>
                            <Button>Analysis</Button>
                        </Panel>
                    </div>
                </div>
            </Container>
        </>
    );
};

export default UserPage;

const Container = styled.div`
    background-color: #2f3237;
    margin: 5%;
    padding: 5%;
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    height: 70vh;
`;

const Panel = styled.div`
    background-color: #1c2124;
    margin-bottom: 10px;
    padding: 10px;
    text-align: center;
    border-radius: 10px;
`;

const Title = styled.div`
    margin: 15px 0;
    font-size: 25px;
`;

const Button = styled.button`
    background-color: #fad000;
    margin-bottom: 10px;
    padding: 5px;
    border-style: none;
    border-radius: 10px;
    width: 150px;
`;
