import { useContext } from "react";
import { FaMicrophone, FaPause, FaPlay } from "react-icons/fa";
import styled from "styled-components";
import MetaContext from "../../MetaContext";
import { Box } from "./Common";
import { responseId } from "./types";

type TaskSectionProps = {
    recording: boolean;
    recordResponse: () => void;
    playing: boolean;
    playResponse: () => void;
    stopEverything: () => void;
    responseAvailable: boolean;
    loadingRecorder: boolean;
};

const TaskSection = (props: TaskSectionProps) => {
    const { narrowWindow } = useContext(MetaContext);
    return (
        <>
            <Box style={{ margin: "10px 0" }}>
                <div id="waveform"></div>
            </Box>
            <div style={{ display: "flex", justifyContent: "center" }}>
                {props.responseAvailable && (
                    <Control
                        onClick={props.playing ? props.stopEverything : props.playResponse}
                        narrowWindow={narrowWindow}
                    >
                        {props.playing ? <FaPause /> : <FaPlay />}
                    </Control>
                )}

                <Control
                    onClick={props.recording ? props.stopEverything : props.recordResponse}
                    narrowWindow={narrowWindow}
                >
                    <FaMicrophone /> &nbsp;
                    {props.loadingRecorder && "Please Wait...."}
                    {props.recording && "Answer now!"}
                    {!(props.loadingRecorder || props.recording) && "Click to answer!"}
                </Control>
            </div>
        </>
    );
};

export default TaskSection;

const Control = styled.button<{ narrowWindow: boolean }>`
    background-color: #414142;
    border-radius: 5px;
    height: 35px;
    align-content: center;
    margin: auto;
    width: 30%;
    border: 1px solid #414142;
    color: #fad000;
    font-size: ${props => (props.narrowWindow ? "12px" : "15px")};
`;
