import { Dispatch, SetStateAction, useState } from "react"
import { FaCaretDown, FaCaretRight, FaCheckCircle, FaCircle, FaTimesCircle } from "react-icons/fa";
import styled from "styled-components"
import { toggleMarkPoint } from "./hooks";
import { MarkSchemeSection, Station_new } from "./types"

type MarkSchemeProps = {
    updateStation: Dispatch<SetStateAction<Station_new>>;
    markScheme: MarkSchemeSection[];
    questionIdx: number;
}

const MarkScheme = (props: MarkSchemeProps) => {

    const [showMarkScheme, updateShowMarkScheme] = useState(true)

    const selectPoint = (pointId: number) => {
        props.updateStation(oldStation => toggleMarkPoint(oldStation, props.questionIdx, pointId))
    }

    const renderSchemePoint = (schemePoint: { point: string, selected: boolean, pointId: number }) => {
        return (
            <SchemePointContainer  onClick = { () => selectPoint(schemePoint.pointId) } >
                <SchemePoint>{ schemePoint.point }</SchemePoint>
                <div>
                    { schemePoint.selected ? <FaCheckCircle color = "green" /> : <FaCircle color = "grey" /> }
                </div>
            </SchemePointContainer>
        )
    }

    return (
        <div style = {{ textAlign: "left" }}>

            <Title onClick = { () => updateShowMarkScheme(!showMarkScheme) }>
                { showMarkScheme ? <FaCaretDown /> : <FaCaretRight /> } 
                &nbsp; Mark Yourself!
            </Title>

            <div style = {{ display: showMarkScheme ? "block" : "none", margin: "5% auto" }}>

                { 
                    props.markScheme.map(x => 
                        <>
                            <h5>{ x.title }</h5>
                            { x.points.map(renderSchemePoint) }
                            <br/>
                        </>
                    )
                }

            </div>


        </div>
    )

}

export default MarkScheme

const Title = styled.button`
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #414142;
    border-radius: 5px;
    height:  35px;
    align-content: center;
    margin: auto;
    width: 100%;
    border-style: none;
    color: white;
`

const SchemePoint = styled.div`
    color: #FAD000;
    font-size: 13px;
    margin: 0 5px;
`

const SchemePointContainer = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
`
