import Header from "./Header"
import { Box, Container } from "./Common"
import { MarkSchemeSection, Station_new } from "./types"
import styled from "styled-components";
import { useState } from "react";
import { countPoints } from "./hooks";
import { FaCaretDown, FaCaretRight, FaCheckCircle, FaCircle, FaTimesCircle } from "react-icons/fa";
import heart from "./media/heart.png";
import lungs from "./media/lungs.png";

type MarkReviewProps = {
    station: Station_new;
}

const MarkReviewPage = (props: MarkReviewProps) => {

    const masterMarkScheme = props.station.questions.map(q => q.markScheme).flat()
    const [selected, total] = countPoints(masterMarkScheme)
    const passed = selected > total * 0.4;

    return (
        <Container>

            <Header
                timedHeader = { false }
                percentage = { 100 }
                briefPrompt = { props.station.brief.prompt }
                title = { "Station One Review - Full Markscheme" }
            />

            <br/>

            <Box >

                <div style = {{ display: "flex", justifyContent: "center", alignItems: "center" }}>

                    <div style = {{ fontSize: "20px", margin: "10px" }}>
                        { passed ? "You: Passed!" : "You did not Pass." }
                    </div>

                    { passed ? <FaCheckCircle color = "green" /> : <FaTimesCircle color = "red" /> }

                </div>

                <br/>

                { 
                    masterMarkScheme.map(section => 
                        <DropDown section = { section } />
                    ) 
                }

                <br/><br/>

                <div style = {{ textAlign: "center" }}>
                    <img src = { lungs } width = { "130px" } />
                </div>

                <ResultsBox>
                    Total : { selected } / { total }
                </ResultsBox>

            </Box>

            </Container>

    )
}

export default MarkReviewPage;

const ResultsBox = styled.div`
    border: 3px solid #FAD000;
    width: 190px;
    margin: auto;
    padding: 5px;
    text-align: center;
    border-radius: 5px;
`

const DropDown = (props: { section: MarkSchemeSection }) => {
    const [expanded, updateExpanded] = useState(true);

    const renderSchemePoint = (schemePoint: { point: string, selected: boolean, pointId: number }) => 
        <div style = {{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "15px" }}>
            <SchemePoint>{ schemePoint.point }</SchemePoint>
            <div>
                { schemePoint.selected ? <FaCheckCircle color = "green" /> : <FaTimesCircle color = "red" /> }
            </div>
        </div>

    return (
        <div style = {{ margin: "10px 0" }} >

            <Title onClick = { () => updateExpanded(!expanded) }>
                { expanded ? <FaCaretDown /> : <FaCaretRight /> }
                &nbsp; { props.section.title }
            </Title>

            <div style = {{ display: expanded ? "block" : "none", margin: expanded ? "20px 0" : "0", padding: "0 10px" }}>
                { props.section.points.map(renderSchemePoint) }
            </div>

        </div>
    )
}

const Title = styled.button`
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #414142;
    border-radius: 5px;
    height:  35px;
    align-content: center;
    margin: auto;
    width: 100%;
    border-style: none;
    color: white;
`

const SchemePoint = styled.div`
    color: #FAD000;
    font-size: 13px;
    margin: 0 5px;
`
