import { useEffect, useState } from "react";
import { Switch, Route, useLocation, Redirect } from "react-router-dom"
import { SarcoidosisStation } from "./DemoStations/SarcoidosisStation";
import OSCEHomePage from "./HomePage";
import StationPage from "./StationPage";
import { MedicalDeviceStation } from "./DemoStations/MedicalDeviceStation";
import { NullStation } from "./DemoStations/NullStation";

const availableStations = [SarcoidosisStation, MedicalDeviceStation]

const OSCEPage = () => {
    const [station, updateStation] = useState(NullStation)

    const location = useLocation()
    useEffect(() => window.scrollTo(0, 0), [location.pathname])

    return (
        <Switch>

            <Route exact path = "/OSCE">
                <OSCEHomePage availableStations = { availableStations } updateStation = { updateStation } />
            </Route>

            <Route path = "/OSCE/Station">
                <StationPage station = { station } updateStation = { updateStation } />
            </Route>

        </Switch>
    );
}

export default OSCEPage;
