import { Dispatch, SetStateAction, useEffect } from "react";
import { FaPause, FaPlay } from "react-icons/fa";
import styled from "styled-components";
import { generateWaveSurfer } from "../hooks";

type TaskSectionProps = {
    playing: boolean;
    playResponse: () => void;
    stopEverything: () => void;
    responseAudio: string;
    questionIdx: number;
};

const TaskSection = (props: TaskSectionProps) => {
    // useEffect(() => {
    //     const container = document.getElementById("waveform" + props.questionIdx);
    //     if (container) container.innerHTML = "";

    //     props.updateAudioState(as => ({
    //         ...as,
    //         responseWaveSurfer: generateWaveSurfer(
    //             "waveform" + props.questionIdx,
    //             props.responseAudio
    //         ),
    //     }));
    // }, [props.responseAudio]);

    return (
        <div style={{ display: "flex", justifyContent: "space-between" }}>
            {!props.responseAudio ? (
                <>
                    <Panel
                        style={{ margin: "10px 0", width: "10%", backgroundColor: "#242528" }}
                    ></Panel>
                    <Panel
                        style={{
                            margin: "10px 0",
                            width: "87%",
                            backgroundColor: "#242528",
                        }}
                    >
                        <div id={"waveform" + props.questionIdx}></div>
                    </Panel>
                </>
            ) : (
                <>
                    <Panel
                        onClick={props.playing ? props.stopEverything : props.playResponse}
                        style={{ margin: "10px 0", width: "10%" }}
                    >
                        {props.playing ? <FaPause /> : <FaPlay />}
                    </Panel>
                    <Panel style={{ margin: "10px 0", width: "87%" }}>
                        <div id={"waveform" + props.questionIdx}></div>
                    </Panel>
                </>
            )}
        </div>
    );
};

export default TaskSection;

const Panel = styled.div`
    background-color: #303237;
    padding: 0 10px;
    border-radius: 10px;
`;
