import { Station_new } from "../types";
import gold from "./media/Sarcoidosis/gold.mp3";
import goldQuestion from "./media/Sarcoidosis/goldQuestion.mp3";
import interpret from "./media/Sarcoidosis/interpret.mp3";
import interpretQuestion from "./media/Sarcoidosis/interpretQuestion.mp3";
import rashes from "./media/Sarcoidosis/rashes.mp3";
import rashesQuestion from "./media/Sarcoidosis/rashesQuestion.mp3";
import bloodtest from "./media/Sarcoidosis/bloodtest.mp3";
import bloodtestQuestion from "./media/Sarcoidosis/bloodtestQuestion.mp3";
import sarcoidosis from './media/Sarcoidosis/sarcoidosis.jpeg'
import ngManagement from "./media/slide_3/ngManagement.mp3";
import ngPosition from "./media/slide_3/ngPosition.mp3";
import tellCorrectPlace from "./media/slide_3/tellCorrectPlace.mp3";
import NGtube from './media/slide_3/NGtube.jpeg'
import abnormality from "./media/slide_4/abnormality.mp3";
import histoCancer from "./media/slide_4/histoCancer.mp3";
import lungcancer from './media/slide_4/lungcancer.webp'

export const SarcoidosisStation: Station_new = {

    title: "Suspicious Chest X-Ray",

    timePermitted: 1000,
    timeRemaining: 1000,

    brief: {
        timePermitted: 60,
        timeRemaining: 60,
        prompt: (
            <div>
                <p>Welcome to the Chest X-Ray interpretation demo!</p>

                <p>Your task is to:</p>

                <p>
                    <div>1. Interpret the data presented to you</div>
                    <div>2. Formulate a working diagnosis</div>
                    <div>3. Answer the associated questions</div>
                </p>
            </div>
        )
    },

    questions: [
        {
            question: "Interpret this CXR and suggest a differential diagnosis",
            questionUrl: interpretQuestion,
            imgsrc: sarcoidosis,
            responseUrl: "",
            exemplarUrl: interpret,
            markScheme: [
                {
                    title: "Patient details",
                    points: [
                        { point: "Identifies patient with 2 pieces of information - eg name and date of birth", selected: false, pointId: 0 },
                        { point: "Identifies date and time Radiograph taken", selected: false, pointId: 1 },
                    ]
                },
                {
                    title: "X-ray interpretation",
                    points: [
                        { point: "Comments on expansion", selected: false, pointId: 2 },
                        { point: "Comments on trachea", selected: false, pointId: 3 },
                    ]
                },
                {
                    title: "Diagnosis",
                    points: [
                        { point: "Bilateral hilar lymphadenopathy", selected: false, pointId: 4 },
                        { point: "Primary differential diagnosis: sarcoidosis", selected: false, pointId: 5 },
                    ]
                }
            ]
        },
        {
            question: "What is the gold standard investigation for Sarcoidosis",
            questionUrl: goldQuestion,
            imgsrc: sarcoidosis,
            responseUrl: "",
            exemplarUrl: gold,
            markScheme: [
                {
                    title: "Gold standard investigation",
                    points: [
                        { point: "Lung biopsy", selected: false, pointId: 0 },
                    ]
                }
            ] 
        },
        {
            question: "Suggest one blood test to support your differential diagnosis",
            questionUrl: bloodtestQuestion,
            imgsrc: sarcoidosis,
            responseUrl: "",
            exemplarUrl: bloodtest,
            markScheme: [
                {
                    title: "Diagnostic test",
                    points: [
                        { point: "Serum Angiotensin Converting Enzyme", selected: false, pointId: 0 },
                    ]
                }
            ]  
        },
        {
            question: "Name two rashes commonly observed in Sarcoidosis",
            questionUrl: rashesQuestion,
            imgsrc: sarcoidosis,
            responseUrl: "",
            exemplarUrl: rashes,
            markScheme: [
                {
                    title: "Rashes associate with lupus",
                    points: [
                        { point: "Erythema nodosum", selected: false, pointId: 0 },
                        { point: "Lupus pernio", selected: false, pointId: 1 },
                    ]
                }
            ] 
        },
    ],
}
