import { Dispatch, SetStateAction, useContext, useEffect, useState } from "react";
import styled from "styled-components";
import MetaContext from "../../MetaContext";
import { Login, LoginButton } from "../HomePage/LoginLogout";
import { StartButton } from "./Common";
import TimerBar from "./TimerBar";
import { HeaderDetails, Question, Station } from "./types";
import htmlParser from "react-html-parser";
import { Link, useHistory, useLocation } from "react-router-dom";
import AlphaTimer from "./AlphaTimer";
import { headerDetailSwitch } from "./headerDetailsSwitch";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";

type HeaderProps = {
    station: Station;
    handleExit: (questions: Question[], tok: string) => void;
    updateQuestionIdx: Dispatch<SetStateAction<{ prev: number; curr: number }>>;
    questionIdx: { prev: number; curr: number };
};

const DefaultDetails: HeaderDetails = {
    title: "Brief",
    percentage: 5,
    exit: false,
    navigator: false,
    timer: false,
    nextFunc: () => {},
    prevFunc: () => {},
};

const Header = (props: HeaderProps) => {
    const { token } = useContext(MetaContext);
    const [displayLoginOption, updateDisplayLoginOption] = useState(false);
    const [headerDetails, updateHeaderDetails] = useState(DefaultDetails);
    const history = useHistory();

    const location = useLocation();
    const splitPaths = location.pathname.split("/");
    const lastPath = splitPaths[splitPaths.length - 1];
    const startTime =
        lastPath === "Brief" ? props.station.brief.timePermitted : props.station.timePermitted;

    useEffect(() => {
        updateHeaderDetails(
            headerDetailSwitch(
                lastPath,
                props.station.questions.length,
                props.questionIdx,
                props.updateQuestionIdx,
                history
            )
        );
    }, [location.pathname, props.questionIdx]);

    return (
        <HeaderContainer>
            <h2>{headerDetails.title}</h2>
            <TimerBar percentage={headerDetails.percentage} />

            <br />

            <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                {headerDetails.exit && (
                    <ExitButton
                        onClick={() =>
                            token
                                ? props.handleExit(props.station?.questions, token)
                                : updateDisplayLoginOption(true)
                        }
                    >
                        Exit
                    </ExitButton>
                )}
                {headerDetails.timer && (
                    <AlphaTimer startTime={startTime} nextFunc={headerDetails.nextFunc} />
                )}
                {headerDetails.navigator && (
                    <div style={{ display: "flex" }}>
                        <Arrow onClick={headerDetails.prevFunc}>
                            <FaArrowLeft />
                        </Arrow>
                        <Arrow onClick={headerDetails.nextFunc}>
                            <FaArrowRight />
                        </Arrow>
                    </div>
                )}
            </div>

            <br />

            {displayLoginOption && (
                <div style={{ textAlign: "center" }}>
                    <Login
                        label={"Login & Save!"}
                        successCallback={t => props.handleExit(props.station.questions, t)}
                    />
                    <Link to="/OSCE">
                        <LoginButton>Exit without saving.</LoginButton>
                    </Link>
                </div>
            )}
        </HeaderContainer>
    );
};

export default Header;

const HeaderContainer = styled.div`
    position: sticky;
    text-align: center;
    top: 0;
    max-width: 800px;
    margin: auto;
    background-color: #1c2124;
    padding: 20px 20px 0 20px;
    opacity: 0.98;
    z-index: 999;
`;

export const Arrow = styled.button`
    text-align: center;
    border: 2px solid grey;
    width: 40px;
    color: #fad000;
    background-color: transparent;
    border-radius: 2px;
    height: max-content;
`;

export const ExitButton = styled.div`
    display: inline-block;
    text-align: center;
    border: 2px solid grey;
    width: 80px;
    color: #fad000;
    border-radius: 2px;
    height: max-content;
`;
