import styled from "styled-components";
import { DataInterpQuestion } from "./types";
import pneumo from "./Pneumothorax.png";
import { Link } from "react-router-dom";
import { useState } from "react";

type AnswerPageProps = {
    question: DataInterpQuestion;
};

const AnswerPage = (props: AnswerPageProps) => {
    const [selectedPoint, updateSelectedPoint] = useState(0);
    console.log(props.question)
    return (
        <>
            <Container>
                <Description>{props.question.task}</Description>
                <Content>
                    <div style = {{ width: "40%" }}>
                        {props.question.sections.map((s, idx) => <Point selected = {idx == selectedPoint}>{s.title}</Point>)}
                    </div>
                    <div><Img src = {pneumo} /></div>
                </Content>
                <Link to = "/DataInterpretation/Answer"><ShowAnswer>Show Answer</ShowAnswer></Link>

            </Container>
        </>
    );
};

export default AnswerPage;

const Container = styled.div`
    text-align: center;
    margin: 50px auto;
    max-width: 800px;
`

const Description = styled.div`
    background-color: grey;
    color: black;
    border-radius: 2px;
    padding: 10px;
`

const Content = styled.div`
    display: flex;
    background-color: #2f3237;
    width: 100%;
    margin: 25px auto;
    padding: 25px;
    border-radius: 2px;
`

const Point = styled.div<{selected: boolean}>`
    background-color: ${props => props.selected ? "#ffb000" : "#2f3237"};
    width: 100%;
    margin: 10px 0px;
    border-radius: 2px;
    cursor: pointer;
`

const Img = styled.img`
    width: 200px;
`

const PointDetail = styled.div`
    background-color: #2f3237;
    width: 100%;
    margin: 25px 0px;
    padding: 25px;
    border-radius: 2px;
`

const ShowAnswer = styled.div`
    background-color: #ffb000;
    color: black;
    border-radius: 2px;
    padding: 10px;
    font-size: 20px;
    cursor: pointer;
`
