import { Dispatch, SetStateAction } from "react";
import { Redirect, Route, Switch, useLocation } from "react-router-dom";
import { TransitionGroup, CSSTransition } from "react-transition-group";
import AlphaHeader from "./AlphaHeader";
import Brief from "./Brief";
import MarkReviewPage from "./MarkReviewPage";
import QuestionPage from "./QuestionPage";
import QuestionReviewPage from "./QuestionReviewPage";
import Summary from "./Summary";
import { AnsweredStationDetails, Question, Station } from "./types";

type StationPageProps = {
    station: Station;
    updateStation: Dispatch<SetStateAction<Station>>;
    answeredStations: AnsweredStationDetails[];
    reviewMode: boolean;
    handleExit: (questions: Question[], tok: string) => void;
};

const StationPage = (props: StationPageProps) => {
    const location = useLocation();

    if (props.station.id === "NullStation") return <Redirect to="/OSCE" />;

    return (
        <>
            <AlphaHeader
                station={props.station}
                updateStation={props.updateStation}
                timedHeader={true}
                percentage={50}
                briefPrompt={""}
                title="station"
                handleExit={props.handleExit}
            />

            <TransitionGroup>
                <CSSTransition key={location.pathname} classNames="page" timeout={500}>
                    <Switch>
                        <Route exact path="/OSCE/Station">
                            <Brief station={props.station} updateStation={props.updateStation} />
                        </Route>

                        {props.station.questions.map((_, questionIdx) => (
                            <Route exact path={"/OSCE/Station/" + questionIdx} key={questionIdx}>
                                <QuestionPage
                                    station={props.station}
                                    updateStation={props.updateStation}
                                    questionIdx={questionIdx}
                                    handleExit={props.handleExit}
                                />
                            </Route>
                        ))}

                        <Route exact path="/OSCE/Station/Summary">
                            <Summary
                                station={props.station}
                                updateStation={props.updateStation}
                                handleExit={props.handleExit}
                            />
                        </Route>

                        {props.station.questions.map((_, questionIdx) => (
                            <Route
                                exact
                                path={"/OSCE/Station/Review" + questionIdx}
                                key={props.station.questions[questionIdx].question}
                            >
                                <QuestionReviewPage
                                    station={props.station}
                                    updateStation={props.updateStation}
                                    questionIdx={questionIdx}
                                    handleExit={props.handleExit}
                                />
                            </Route>
                        ))}

                        <Route exact path="/OSCE/Station/MarkReview">
                            <MarkReviewPage
                                station={props.station}
                                updateStation={props.updateStation}
                                reviewMode={props.reviewMode}
                                handleExit={props.handleExit}
                            />
                        </Route>
                    </Switch>
                </CSSTransition>
            </TransitionGroup>
        </>
    );
};

export default StationPage;
