import axios from "axios";
import { useContext } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import MetaContext from "../../MetaContext";

type ChekoutProps = {};

const Chekout = (props: ChekoutProps) => {
    const { SERVER_URL, CLIENT_URL, token } = useContext(MetaContext);

    const handleCheckout = () => {
        if (!token) return alert("Please sign-in first!");

        axios
            .post(SERVER_URL + "/pay/create-checkout-session", {
                cancel_url: CLIENT_URL + "/Subscribe/PaymentCanceled",
                success_url: CLIENT_URL + "/Subscribe/PaymentSuccess",
                token,
            })
            .then(res => {
                window.location.href = res.data;
            })
            .catch(console.log);
    };

    return (
        <div style={{ background: "#2f3237", margin: "5%", padding: "5%", textAlign: "center" }}>
            <h4>Get full access to our OSCE stations for Â£45 / year!</h4>

            <br />

            <CheckoutButton color="#FAD000" minWidth={300} onClick={handleCheckout}>
                Go to Checkout!
            </CheckoutButton>

            <br />

            <Link to="/OSCE">
                <CheckoutButton color="red" minWidth={150}>
                    Cancel
                </CheckoutButton>
            </Link>
        </div>
    );
};

export default Chekout;

const CheckoutButton = styled.button<{ color: string; minWidth: number }>`
    display: block;
    margin: auto;
    border-style: none;
    border: 2px solid ${props => props.color};
    background-color: transparent;
    color: ${props => props.color};
    border-radius: 5px;
    padding: 5px;
    min-width: ${props => props.minWidth}px;
    &:hover {
        background-color: ${props => props.color};
        color: black;
    }
`;
