import { Dispatch, SetStateAction, useEffect } from "react";
import { Redirect } from "react-router-dom";
import { Timer } from "./Common";
import { useLoopTime } from "./hooks";
import { Station } from "./types";

type TimerApparatusProps = {
    station: Station;
    questionIdx: number;
};

const TimerApparatus = (props: TimerApparatusProps) => {
    const pathToNext =
        props.questionIdx === props.station.questions.length - 1
            ? "/OSCE/Summary"
            : "/OSCE/" + (props.questionIdx + 1);
    const secondTicker = useLoopTime(props.station.timeRemaining - 1);

    const convertToString = (time: number) => {
        const minutes = Math.floor(time / 60);
        const minuteString = minutes < 10 ? "0" + minutes : "" + minutes;
        const seconds = time % 60;
        const secondsString = seconds < 10 ? "0" + seconds : "" + seconds;
        return minuteString + ":" + secondsString;
    };

    if (secondTicker <= 0) return <Redirect to={pathToNext} />;

    return (
        <div style={{ textAlign: "center" }}>
            <Timer> {convertToString(secondTicker)}</Timer>
        </div>
    );
};

export default TimerApparatus;
