import { Dispatch, SetStateAction, useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import WaveSurfer from "wavesurfer.js";
import MetaContext from "../../MetaContext";
import { Box, MoveOn, Container } from "./Common";
import { generateWaveSurfer } from "./hooks";
import MarkScheme from "./MarkScheme";
import QuestionPrompt from "./QuestionPrompt";
import TaskSectionReview from "./TaskSectionReview";
import { Station, exemplarId, responseId, promptId, Question } from "./types";
import { CSSTransition } from "react-transition-group";
import "./styles.css";

type QuestionReviewPageProps = {
    station: Station;
    updateStation: Dispatch<SetStateAction<Station>>;
    questionIdx: number;
    handleExit: (questions: Question[], tok: string) => void;
    match: boolean;
};

type AudioState = {
    promptAudio: HTMLAudioElement | null;
    promptPlaying: boolean;
    exemplarWaveSurfer: WaveSurfer | undefined;
    responseWaveSurfer: WaveSurfer | undefined;
    exemplarPlaying: boolean;
    responsePlaiyng: boolean;
};

const initalAudioState: AudioState = {
    promptAudio: null,
    promptPlaying: true,
    exemplarWaveSurfer: undefined,
    responseWaveSurfer: undefined,
    exemplarPlaying: false,
    responsePlaiyng: false,
};

const QuestionReviewPage = (props: QuestionReviewPageProps) => {
    const { SERVER_URL } = useContext(MetaContext);
    const [imageExpanded, updateImageExpanded] = useState(false);
    const [audioState, updateAudioState] = useState<AudioState>(initalAudioState);

    const percentage = 10 + (100 * props.questionIdx) / props.station.questions.length;
    const question = props.station.questions[props.questionIdx];
    const pathToNext =
        props.questionIdx === props.station.questions.length - 1
            ? "/OSCE/Station/MarkReview"
            : "/OSCE/Station/Review" + (props.questionIdx + 1);
    const pathToPrev =
        props.questionIdx === 0
            ? "/OSCE/Station/Summary"
            : "/OSCE/Station/Review" + (props.questionIdx - 1);

    const stopAllPlayback = () => {
        audioState.promptAudio?.pause();
        audioState.exemplarWaveSurfer?.stop();
        audioState.responseWaveSurfer?.stop();
        updateAudioState(as => ({
            ...as,
            exemplarPlaying: false,
            responsePlaiyng: false,
            promptPlaying: false,
        }));
    };

    const play = (id: string) => {
        stopAllPlayback();
        updateAudioState(as => ({
            ...as,
            promptPlaying: id == promptId,
            exemplarPlaying: id === exemplarId,
            responsePlaiyng: id === responseId,
        }));

        if (id === exemplarId) audioState.exemplarWaveSurfer?.play();
        if (id === promptId) audioState.promptAudio?.play();
        if (id === responseId) audioState.responseWaveSurfer?.play();
    };

    useEffect(() => {
        [exemplarId, responseId].forEach(id => {
            const container = document.getElementById(id);
            if (container) container.innerHTML = "";
        });

        updateAudioState({
            ...initalAudioState,
            promptAudio: document.getElementById(promptId) as HTMLAudioElement,
            exemplarWaveSurfer: generateWaveSurfer(
                exemplarId,
                question.exemplarAudio,
                stopAllPlayback
            ),
            responseWaveSurfer: generateWaveSurfer(
                responseId,
                question.responseAudio,
                stopAllPlayback
            ),
        });
    }, []);

    // cease playback when compnent unmounts <=== six hour bug :)
    useEffect(
        () => stopAllPlayback,
        [audioState.exemplarWaveSurfer, audioState.responseWaveSurfer]
    );

    return (
        <CSSTransition in={props.match} timeout={1000} classNames="page" unmountOnExit>
            <Container style={{ textAlign: "center" }} className="page">
                <Box style={{}}>
                    <QuestionPrompt
                        question={question}
                        toggle={() => {
                            audioState.promptPlaying ? stopAllPlayback() : play(promptId);
                        }}
                    />

                    <br />
                    <br />

                    <div style={{ textAlign: "center", position: "relative" }}>
                        <Img
                            src={question.img}
                            imageExpanded={imageExpanded}
                            onClick={() => updateImageExpanded(!imageExpanded)}
                        ></Img>

                        <div
                            style={{ position: "absolute", top: -25, width: "100%", color: "grey" }}
                        >
                            Click to {imageExpanded ? "shrink" : "expand"}!
                        </div>
                    </div>

                    <br />

                    <div style={{ color: "grey" }}>Exemplar Answer</div>

                    <TaskSectionReview
                        containerId={exemplarId + props.questionIdx}
                        playing={audioState.exemplarPlaying}
                        play={() => play(exemplarId)}
                        stop={stopAllPlayback}
                    />

                    <br />

                    <div style={{ color: "grey" }}>Your Answer</div>

                    <TaskSectionReview
                        containerId={responseId}
                        playing={audioState.responsePlaiyng}
                        play={() => play(responseId)}
                        stop={stopAllPlayback}
                    />

                    <br />
                    <br />

                    <MarkScheme
                        questionIdx={props.questionIdx}
                        markScheme={question.markScheme}
                        updateStation={props.updateStation}
                    />

                    <br />

                    <div style={{ display: "flex", justifyContent: "space-between" }}>
                        <div style={{ width: "100px" }}>
                            <Link to={pathToPrev}>
                                <MoveOn>Go Back</MoveOn>
                            </Link>
                        </div>

                        <div style={{ width: "100px" }}>
                            <Link to={pathToNext}>
                                <MoveOn>Move On</MoveOn>
                            </Link>
                        </div>
                    </div>
                </Box>
            </Container>
        </CSSTransition>
    );
};

export default QuestionReviewPage;

const Img = styled.img<{ imageExpanded: boolean }>`
    display: block;
    margin: auto;
    width: ${props => (props.imageExpanded ? "100%" : "60%")};
    transition: width 0.2s;
    &:hover {
        cursor: pointer;
    }
`;
